kontrollformel:
sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val harGlobalSkatteplikt = skattepliktGjeldende
              .forForekomsterAv { skatteplikt }
              .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

        // kontrollen gir utslag dersom skattyter ikke har kort med lønn fra utlandet
        // og ligger i kontrolliste
        // og har global skatteplikt
        skattemelding
            .utslagFor {

            val harIngenForekomsterAvUtenlandskLoenn = forForekomsterAv{arbeidsgiver}
                .harIngenForekomsterDer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }

            val utenlandskLoennIkontrolliste = finnesIKontrolliste(
                "Gld med loenn i Utlandet",
                listeAv(partsnr)
            )

            hvis(harGlobalSkatteplikt og
                 harIngenForekomsterAvUtenlandskLoenn og
                 utenlandskLoennIkontrolliste) {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtvidetVeiledningPaaAKU = utvidetVeiledning
            .forForekomsterAv { utvidetveiledning }
            .forForekomsterAv { metadata }
            .hvorMinstEnForekomst { type.finnesBlant("AKU_KJENT_BELOEP", "AKU_UKJENT_BELOEP") }

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        // Kontrollen gir utslag dersom skattyter ligger i listen over skattytere med utenlandsk pensjon - AKU
        // og ikke har UV for AKU
        // og ikke har oppgitt pensjon i utlandet
        // kontrollen gir merknad i SME

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val partFinnesIkontrolliste = finnesIKontrolliste(
                "kontrolliste - Utenlandsk pensjon AKU", listeAv(partsnr)
            )

            val harPensjonFraUtlandet = forForekomsterAv(pensjonsinntekt)
                .hvorMinstEnForekomst { (landkode er oppgitt) og (landkode erUlik "NO") }

            hvis(
                partFinnesIkontrolliste og
                    ikke(harUtvidetVeiledningPaaAKU) og
                    ikke(harPensjonFraUtlandet)
            )
            {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert annen skattepliktig kapitalinntekt på annet finansprodukt
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .filtrer { grunnlag(annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall) erStørreEnn 0 }
            .utslagFor {
                val differanseGevinst =
                    (
                        grunnlag(annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall) - fastsatt(
                            annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall
                        )
                        )

                hvis(ikke(harInnsending) og (differanseGevinst erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har egenfastsatt tap på annet finansprodukt
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .forForekomsterAv(
                sti(
                    skattepliktigTapVedRealisasjonAvAnnetFinansprodukt,
                    beloepUtenHensynTilValgtPrioritertFradragstype
                )
            )
            .filtrer { grunnlag(beloepSomTall) erLik 0 }
            .utslagFor {
               val differanseTap = (fastsatt(beloepSomTall) - grunnlag(beloepSomTall))

                hvis(differanseTap erStørreEnnEllerLik 50_000) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret formue på annet finansprodukt med 10 mill eller mer
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .utslagFor {
                val differanseGevinst = differanse(
                    grunnlag(formuesverdiForAnnetFinansprodukt.beloepSomTall),
                    fastsatt(formuesverdiForAnnetFinansprodukt.beloepSomTall)
                )

                hvis(ikke(harInnsending) og (differanseGevinst erStørreEnnEllerLik 10_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret gevinst på annet finansprodukt med 2 mill eller mer
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .utslagFor {
                val differanseGevinst =
                    differanse(
                        grunnlag(skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepSomTall),
                        fastsatt(skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepSomTall)
                    )

                hvis(ikke(harInnsending) og (differanseGevinst erStørreEnnEllerLik 2_000_000)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret tap på annet finansprodukt med 50 000 eller mer
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .forForekomsterAv(sti(skattepliktigTapVedRealisasjonAvAnnetFinansprodukt, fradragsberettigetBeloep))
            .utslagFor {
                val differanseTap = differanse(grunnlag(beloepSomTall), fastsatt(beloepSomTall))

                hvis(ikke(harInnsending) og (differanseTap erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val harUtslagPaaKontroll =
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_IKKE_MERKNAD") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_IKKE_MERKNAD") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_IKKE_MERKNAD") eller
         utslagPåKontroll("KRYPTO_OPPLYSNINGER_FIRI_TIPS") eller
         utslagPåKontroll("KRYPTO_OPPLYSNINGER_FIRI_IKKE_MERKNAD") eller
         utslagPåKontroll("VIRTUELL_VALUTA_KRYPTOVALUTA_GEV_TAP_I_FJOR_IKKE_MERKNAD") eller
         utslagPåKontroll("VIRTUELL_VALUTA_KRYPTOVALUTA_GEV_TAP_I_FJOR")

        // kontrollen gir utslag når skattepliktige egenfastsetter underkort "Virtuell valuta og kryptovaluta" uten beløp i noen av feltene, men med vedlegg
        // eller hvis den skattepliktige egenfastsetter kort uten beløp og uten vedlegg når der er lagt inn
        //        antall i felt "Antall per 31. desember i inntektsåret"
        //        og/eller felt "navn på virtuelle eiendeler/ kryptovaluta"
        // skal ikke gi utslag hvis det er utslag på en av de gitte kontrollene
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(virtuellEiendel)
            .utslagFor {
                val harVedlegg = hvorMinstEnForekomst { gjelderFlereVirtuelleEiendeler har vedleggPåFelt }

                val ikkeHarBeloep = forAlleForekomster
                    .hvorMinstEnForekomst { (beloepSomTall erUlik 0) eller (beloepIValuta erUlik 0) }
                    .ikke()

                val harAngittAntallEllerNavn = hvorMinstEnForekomst { (antall erStørreEnn 0) eller (navn er oppgitt) }


                hvis(((ikkeHarBeloep og harVedlegg) eller
                     (ikkeHarBeloep og ikke(harVedlegg) og harAngittAntallEllerNavn)) og
                     ikke(harUtslagPaaKontroll)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harFordringFjoraaret = fjoraaretsSkattemelding
            .forForekomsterAv { annenFordring }
            .hvorMinstEnForekomst {
                    (fastsatt(opptjenteRenter.beloep) erStørreEnn 0) eller
                    (fastsatt(formuesverdi.beloep) erStørreEnn 0)
            }

        // kontrollen gir utslag dersom skattyter hadde fordring i fjor, men ikke har oppgitt noen fordring i år
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

            val harFordringIAar = forForekomsterAv{annenFordring}
                .hvorMinstEnForekomst {
                    (fastsatt(opptjenteRenter.beloep) erStørreEnn 0) eller
                    (fastsatt(formuesverdi.beloep) erStørreEnn 0)
                }

            hvis(ikke(harFordringIAar) og
                 harFordringFjoraaret)
            {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
       val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

       val harUtslagPaaKontroll = utslagPåKontroll("EMIGRASJON_AVTALEBOSTED_SAMME_DATO")

       val kortUtenEndringsaarsakBosatt = skattemelding
            .forForekomsterAv{
                grupper(
                    arbeidsgiver,
                    pensjonsinntekt,
                    konto,
                    aksjeIkkeIAksjonaerregisteret,
                    aksjeIAksjonaerregisteret,
                    verdipapirfond,
                    obligasjonOgSertifikat,
                    aksjesparekonto,
                    annetFinansprodukt,
                    skattefavorisertIndividuellSparingTilPensjon,
                    annenKapitalinntektOgKostnad
                )
            }
                .forForekomsterAv{aarsakTilEndring}
                .harIngenForekomsterDer { aarsak erLik "bosattEtterSkatteavtaleIAnnetLand" }

           val eiendomskortUtenEndringsaarsakBosatt = skattemelding
            .forForekomsterAv{fastEiendom}
            .forForekomsterAv{grupper(fastEiendomSomFormuesobjekt, fastEiendomSomUtleieobjekt, fastEiendomSomRealisasjonsobjekt)}
            .forForekomsterAv{aarsakTilEndring}
            .harIngenForekomsterDer { aarsak erLik "bosattEtterSkatteavtaleIAnnetLand" }

        // kontrollen gir utslag dersom skattyter har oppgitt tilleggssinformasjon om Bosatt etter skatteavtale i annet land,
        // men IKKE har oppgitt endringsårsak  "Jeg er bosatt etter skatteavtale i et annet land enn Norge" på noe kort
        // skal IKKE gi utslag hvis skatteplikt Begrenset
        // kontrollen gir merknad i SME
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale}
            .utslagFor {
                val harTilleggsinfoOmBosatt = hvorMinstEnForekomst {
                        (bosattILandEtterSkatteavtale er oppgitt) eller
                        (startdatoForBosattIUtlandEtterSkatteavtale er oppgitt) eller
                        (sluttdatoForBosattIUtlandEtterSkatteavtale er oppgitt) eller
                        (betalerSkattTilBosattlandEtterSkatteavtale er oppgitt)
                }

            hvis(ikke(harUtslagPaaKontroll) og ikke(harBegrensetSkatteplikt) og
                    harTilleggsinfoOmBosatt og kortUtenEndringsaarsakBosatt og eiendomskortUtenEndringsaarsakBosatt )
              {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
              }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag og merknad når skattyter har lagt til andre forvaltningskostnader > 0
        // Skattepliktig har beskrivelse som inneholder ord fra liste
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{forvaltningskostnad}
            .utslagFor {

          val harBeskrivelseSomInneholderOrdUnderForvaltningskostnader =
                    hvorMinstEnForekomst {
                        (fastsatt(forvaltningskostnadForVerdipapir.fradragsberettigetBeloep.beloep erStørreEnn 0) og
                        ((beskrivelseAvForvaltningskostnad.inneholder("adsl")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("internettabonnement")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("internettkostnader")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("internet")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("internett")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("ipad")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("nettbrett")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("laptop")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("mobil")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("mobiltelefon")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("mobilregninger")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("tlf")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("bredbånd")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("pc")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("data")) eller
                         (beskrivelseAvForvaltningskostnad.inneholder("telefon")))
                        )
                    }

            hvis(harBeskrivelseSomInneholderOrdUnderForvaltningskostnader
                )
                {
               betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

       // hvis sluttbruker har lagt inn annenKapitalinntekt UTEN beskrivelse av inntektene
       skattemelding
            .forForekomsterAv{annenKapitalinntektOgKostnad}
            .utslagFor {

                val manglerBeskrivelsePaaAnnenKapitalinntekt = hvorMinstEnForekomst {
                        (inntekt.beloep erStørreEnn 0) og
                        (beskrivelseAvInntekt ikkeEr oppgitt)}

                hvis(manglerBeskrivelsePaaAnnenKapitalinntekt) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag og merknad når Skattepliktig krever > 0 i kostnadsfeltet i kortet "Andre kapitalinntekter utenom lønnstakerforhold og næringsvirksomhet"
        // Skattepliktig har beskrivelse som inneholder ord fra liste
         skattemelding
            .forForekomsterAv{annenKapitalinntektOgKostnad}
            .utslagFor {

             val harBeskrivelseSomInneholderOrdUnderAndreKapitalkostnader = hvorMinstEnForekomst {
                        (kostnad.fradragsberettigetBeloep.beloep erStørreEnn 0) og
                         ((beskrivelseAvKostnad.inneholder("minstefradrag")) eller
                         (beskrivelseAvKostnad.inneholder("minste fradrag")) eller
                         (beskrivelseAvKostnad.inneholder("personfradrag")) eller
                         (beskrivelseAvKostnad.inneholder("forskuddstrekk")) eller
                         (beskrivelseAvKostnad.inneholder("finnmarksfradrag")) eller
                         (beskrivelseAvKostnad.inneholder("fremførbart underskudd")) eller
                         (beskrivelseAvKostnad.inneholder("boligsparing for ungdom")) eller
                         (beskrivelseAvKostnad.inneholder("BSU")) eller
                         (beskrivelseAvKostnad.inneholder("skattetrekk")) eller
                         (beskrivelseAvKostnad.inneholder("lønnstrekk")) eller
                         (beskrivelseAvKostnad.inneholder("eiendomsskatt")) eller
                         (beskrivelseAvKostnad.inneholder("minst fradrag")) eller
                         (beskrivelseAvKostnad.inneholder("Minimum deduction")) eller
                         (beskrivelseAvKostnad.inneholder("Minimum standard deduction"))
                      )
                    }

                hvis(harBeskrivelseSomInneholderOrdUnderAndreKapitalkostnader)
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, skjulerSkatteberegning)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val kortForMinstefradrag = skattemelding
           .forForekomsterAv{minstefradragOgKostnaderKnyttetTilArbeidOgAnnenInntekt}

        // Kontrollen gir utslag og merknad når Skattepliktig krever fradrag for kostnader i kort for andre kapitalinntekter, og
        // ikke oppgir inntekt i kortet, og
        // beskrivelsen av kostnaden inneholder minst ett av følgende ord fra liste
        skattemelding
            .forForekomsterAv{annenKapitalinntektOgKostnad}
            .utslagFor {

         val harKostnaderKnyttetTilKapitalinntektIkkeInntekt = hvorMinstEnForekomst {
                         (kostnad.fradragsberettigetBeloep.beloep erStørreEnn 0) og
                        ((inntekt.beloep ikkeEr oppgitt) eller
                         (inntekt.beloep erLik 0)) og
                         ((beskrivelseAvKostnad.inneholder("skattemelding")) eller
                         (beskrivelseAvKostnad.inneholder("skatte melding")) eller
                         (beskrivelseAvKostnad.inneholder("selvangivelse")) eller
                         (beskrivelseAvKostnad.inneholder("selv angivelse")) eller
                         (beskrivelseAvKostnad.inneholder("ligning")) eller
                         (beskrivelseAvKostnad.inneholder("sjølvmelding")) eller
                         (beskrivelseAvKostnad.inneholder("sjølv melding")) eller
                         (beskrivelseAvKostnad.inneholder("likning")) eller
                         (beskrivelseAvKostnad.inneholder("tax return")))
                      }

            hvis(harKostnaderKnyttetTilKapitalinntektIkkeInntekt)
                {
                betjeningsstrategiSettesTil(ikke, merknadStandard,
                                            Lenke settesTil kortForMinstefradrag)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val harUtslagPaaKontroll = utslagPåKontroll("ANDRE_KAPITALKOSTNADER_BISTAND_SKATTEMELDING_ARBEIDSINNTEKT")

       val kortMedForvaltningskostnad = skattemelding
           .forForekomsterAv{forvaltningskostnad}

        // Kontrollen gir utslag hvis utslag på ANDRE_KAPITALKOSTNADER_BISTAND_SKATTEMELDING_ARBEIDSINNTEKT
        skattemelding
           .utslagFor {

        hvis(harUtslagPaaKontroll)
                {
                betjeningsstrategiSettesTil(ikke, merknadStandard,
                                             Lenke settesTil kortMedForvaltningskostnad)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag og merknad når Skattepliktig krever > 0 i kostnadsfeltet i kortet "Andre kapitalinntekter utenom lønnstakerforhold og næringsvirksomhet"
        // Skattepliktig har beskrivelse som inneholder nøyaktige ikke case sensitive ord fra liste eller
        //beskrivelsen er 2 bokstaver eller mindre eller
        //beskrivelsen er bare tall eller spesialtegn

         skattemelding
            .forForekomsterAv{annenKapitalinntektOgKostnad}
            .utslagFor {

         val harBeskrivelseSomInneholderOrdUnderAndreKapitalkostnader = hvorMinstEnForekomst {
                        (kostnad.fradragsberettigetBeloep.beloep erStørreEnn 0) og
                        ((beskrivelseAvKostnad.formatert.lengde erMindreEnn 3) eller
                         (beskrivelseAvKostnad.erBareTallEllerSpesialTegn) eller
                        ((beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"utgifter")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"ugifter")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"utgift")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"ekstra utgifter")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"ekstra kostnad")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"ekstra kostnader")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"kostnad")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"kostnader")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"diverse")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"div")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"div.")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"fradrag")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"fradag")) eller
                         (beskrivelseAvKostnad.erLik(ikkeCaseSensitiv,"deduction")))
                      )
                    }

                hvis(harBeskrivelseSomInneholderOrdUnderAndreKapitalkostnader)
                {
