sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
         val harUtslagPaaKontroll = utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING") eller
                                    utslagPåKontroll("AVTALEBOSTED_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD")

         val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        // skattepliktig har lagt minst et kort hvor det er registrert "Kreditfradrag" i feltet "Metode ved dobbeltbeskatning"
        // og har lagt til kort for kreditfradrag under tema "Metode ved dobbeltbeskatning
        // og har lagt til kort for avtalebosted under tema Andre forhold
        // og har ingen kort hvor et av alternativene for "Ikke skattepliktig" er lagt til i felt for "Trygde- og skattemessig unntak"
        // skal ikke gi utslag hvis utslag på SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING
        // skal heller ikke gi utslag hvis utslag på på AVTALEBOSTED_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD
        // skal IKKE gi utslag hvis skatteplikt Begrenset.  
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val harBosattland =
                forForekomsterAv(tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale)
                    .hvorMinstEnForekomst { bosattILandEtterSkatteavtale er oppgitt }

            val harMetodeDobbeltbeskatningKredit =
                forAlleForekomster
                    .hvorMinstEnForekomst {
                        (fastsatt(metodeVedDobbeltbeskatning) erLik "kredit") og
                            (
                                fastsatt(trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriUtenTrygdeavgiftsplikt",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                                )
                                )
                    }

            val harKravOmKreditfradragForUtenlandskSkatt =
                hvorMinstEnForekomst { kravOmKreditfradragForUtenlandskSkatt er oppgitt }

            hvis(
                ikke(harUtslagPaaKontroll) og ikke(harBegrensetSkatteplikt) og
                    harMetodeDobbeltbeskatningKredit og harKravOmKreditfradragForUtenlandskSkatt og
                    harBosattland
            )
            {
                betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
            }
        }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .importer(
        SLUTTDATO_I_INNTEKTSAARET,
        ALLE_FELT_MED_EGENSKAPER
    ) { (D_sluttdatoIInntektsaaret, D_alleFeltMedEgenskaper) ->

        val harUtslagPaaKontroll =
            utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING") eller
                    utslagPåKontroll("AVTALEBOSTED_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD")

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        // skattepliktig har lagt minst et kort hvor det er registrert "skattenedsettelse etter ettårsregel" eller "skattenedsettelse etter skatteavtale" i feltet "Metode ved dobbeltbeskatning"
        // og har lagt til kort for "skattenedsettelse etter ettårsregel" eller "skattenedsettelse etter skatteavtale" under tema "Metode ved dobbeltbeskatning
        // og har lagt til kort for avtalebosted under tema Andre forhold
        // og har ingen kort hvor et av alternativene for "Ikke skattepliktig" er lagt til i felt for "Trygde- og skattemessig unntak"
        // skal ikke gi utslag hvis utslag på SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING
        // skal heller ikke gi utslag hvis utslag på AVTALEBOSTED_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD
        // skal ikke gi utslag hvis startdato og sluttdato i kort for "skattenedsettelse etter ettårsregel"/ "skattenedsettelse etter skatteavtale" ikke overlapper med startdato og sluttdato      i kort for avtalebosted
        //(f.eks. skattenedsettelse 01.01.2023 - 01.04.2023 og avtalebosted 05.05.2023 - 31.12.2023) - overlapper periodene skal det være utslag (f.eks. 01.01.2023 - 01.04.2023 og   01.02.2023 - 31.12.2023)
        // skal IKKE gi utslag hvis skatteplikt Begrenset.
        skattemelding
            .utslagFor {

                val alleKortForekomster = D_alleFeltMedEgenskaper
                  .forForekomsterAv { kort }

                val harSkattefriOgEttaarsregelEllerAlternativFordeling = alleKortForekomster
                  .forForekomsterAv { kortMedEgenskaper }
                  .hvorMinstEnForekomst {
                      (fastsatt(metodeVedDobbeltbeskatning).finnesBlant(
                           "denAlternativeFordelingsmetoden",
                           "ettaarsregelen")) og
                      (fastsatt(trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                           "skattefriMedTrygdeavgiftspliktForHelsedel",
                           "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                           "skattefriMedTrygdeavgiftsplikt",
                           "skattefriKildeskattPaaPensjon",
                           "skattefriUtenTrygdeavgiftsplikt",
                           "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"))
                  }

                val harSkattefriOgEttaarsregelEllerAlternativFordelingISubkort = alleKortForekomster
                   .forForekomsterAv{ subkort }
                   .forForekomsterAv { kortMedEgenskaper }
                   .hvorMinstEnForekomst {
                       (fastsatt(metodeVedDobbeltbeskatning).finnesBlant(
                           "denAlternativeFordelingsmetoden",
                           "ettaarsregelen")) og
                      (fastsatt(trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                           "skattefriMedTrygdeavgiftspliktForHelsedel",
                           "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                           "skattefriMedTrygdeavgiftsplikt",
                           "skattefriKildeskattPaaPensjon",
                           "skattefriUtenTrygdeavgiftsplikt",
                           "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"))
                   }

                val harOppgittTillleggsinfoEttaarsregelEllerAlternativFordeling =
                   hvorMinstEnForekomst {
                        gruppe(
                            tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen,
                            tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden
                        ) er oppgitt
                    }

                val harOppgittTillleggsinfoBosattIUtlandEtterSkatteavtale = hvorMinstEnForekomst { tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale er oppgitt }

                val ettaarsregelForekomst = forForekomsterAv { tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen }
                   .forForekomsterAv { arbeidsforholdIUtlandet }

                val tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtaleForekomst = forForekomsterAv { tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale }

                val startdatoDenAlternativeFordelingsmetoden = forForekomsterAv { tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden}
                   .verdiFor { fastsatt(startdatoForOppholdIUtland) }

                val sluttdatoDenAlternativeFordelingsmetoden = forForekomsterAv {tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden}
                   .verdiFor { fastsatt(sluttdatoForOppholdIUtland) }

                val harDatoBosattIUtlandetSomOverlapperPeriodeMedDobbeltbeskatning = forForekomsterAv { tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen }
                   .forForekomsterAv { arbeidsforholdIUtlandet }
                   .hvorMinstEnForekomst { forekomstArbeidsforholdIUtlandet ->
                        val startdatoEttaarsregelen = forekomstArbeidsforholdIUtlandet
                           .verdiFor { fastsatt(startdato) }

                        val sluttdatoEttaarsregelen = forekomstArbeidsforholdIUtlandet
                           .verdiFor { fastsatt(sluttdato) }

                            tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtaleForekomst
                                .hvorMinstEnForekomst { forekomstTilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale ->

                                    val startdatoBosattIUtlandet = forekomstTilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale
                                       .verdiFor {fastsatt(startdatoForBosattIUtlandEtterSkatteavtale)}

                                    val sluttdatoBosattIUtlandet = forekomstTilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale
                                       .verdiForEllerDefault(D_sluttdatoIInntektsaaret) {fastsatt(sluttdatoForBosattIUtlandEtterSkatteavtale)}

                                    (startdatoBosattIUtlandet er oppgitt) og (sluttdatoBosattIUtlandet er oppgitt) og
                                    ((startdatoBosattIUtlandet erEtterEllerLik startdatoEttaarsregelen) og (startdatoBosattIUtlandet erFør sluttdatoEttaarsregelen) eller
                                    ((startdatoBosattIUtlandet erEtterEllerLik startdatoDenAlternativeFordelingsmetoden) og (startdatoBosattIUtlandet erFør sluttdatoDenAlternativeFordelingsmetoden)))
                                }
                        }

                val harDatoEttaarsregelen = ettaarsregelForekomst
                    .hvorMinstEnForekomst {(fastsatt(startdato) er oppgitt) og (fastsatt(sluttdato) er oppgitt)}

                val harDatoDenAlternativeFordelingsmetoden = forForekomsterAv {tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden}
                    .hvorMinstEnForekomst {(fastsatt(startdatoForOppholdIUtland) er oppgitt) og (fastsatt(sluttdatoForOppholdIUtland) er oppgitt)}

                hvis(
                    ikke(harUtslagPaaKontroll) og ikke(harBegrensetSkatteplikt) og
                    (harSkattefriOgEttaarsregelEllerAlternativFordeling eller harSkattefriOgEttaarsregelEllerAlternativFordelingISubkort) og
                    harOppgittTillleggsinfoEttaarsregelEllerAlternativFordeling og
                    harOppgittTillleggsinfoBosattIUtlandEtterSkatteavtale og
                    ((harDatoEttaarsregelen eller harDatoDenAlternativeFordelingsmetoden) og harDatoBosattIUtlandetSomOverlapperPeriodeMedDobbeltbeskatning)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
    }

