                val harSkattepliktigUtbyttePaaVerdipapirfondUtenRettUnntak = skattemelding
                   .fokusområde("Fokusområde")
                   .forForekomsterAv{verdipapirfond}
                   .hvorMinstEnForekomst{(fastsatt(skattepliktigUtbytte.beloep) er oppgitt) og
                                         (fastsatt(skattepliktigUtbytte.trygdeEllerSkattemessigUnntak erUlik "utbytteOmfattetAvSkatteavtaleVedSkatteavtalebostedIAnnenStat"))}

            hvis(ikke(harBegrensetSkatteplikt) og
                 harTilleggsinfoOmBosattIGitteLand og
