sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val sumTrygdeavift = beregnetSkattDokument
            .summer { beregnetSkatt.skattOgAvgift.sumTrygdeavgift.beloep }

         // hvis kort for avtalebosted under Andre forhold er registrert
        // og har lagt til kort for avtalebosted under tema Andre forhold
         // og skattyter har pensjon/trygd
         // skal ikke gi utslag dersom pensjon er satt trygdefri
         // skal bare gi utslag dersom fastatt beregnet trygdeavgift er større enn 0
         // skal ikke gi utslag hvis skattepliktig har skatteplikt Begrenset
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(pensjonsinntekt)
            .utslagForFørste {
                val sumPensjon = forForekomsterAv(perMaaned)
                    .summer { fastsatt(pensjonsinntektSomGruppe.beloepSomTall) }

                val harIngenForekomstAvPensjonMedTrygdefritak = forForekomsterAv(perMaaned)
                    .harIngenForekomsterDer {
                        fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattepliktigUtenTrygdeavgiftsplikt"
                        )
                    }

                val harBosattland = skattemeldingUtgått
                  .forForekomsterAv(tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale)
                  .hvorMinstEnForekomst { bosattILandEtterSkatteavtale er oppgitt }

                hvis(
                    ikke(harBegrensetSkatteplikt) og
                         harBosattland og
                        (sumTrygdeavift erStørreEnn 0) og
                        (sumPensjon erStørreEnn 0) og
                        harIngenForekomstAvPensjonMedTrygdefritak
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

         // hvis kort for avtalebosted under Andre forhold er registrert
         // og land i kortet for avtalebosted er Thailand
         // og sum grunnlag for pensjon/trygd er større enn sum fastsat
         // skal ikke gi utslag hvis skattepliktig har skatteplikt Begrenset
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pensjonsinntekt)
            .utslagFor {
                val sumPensjonPerUtbetaler = forForekomsterAv(perMaaned)
                    .summer { pensjonsinntektSomGruppe.beloepSomTall }

                val pensjonGrunnlagMinusFastsatt =
                    grunnlag(sumPensjonPerUtbetaler) - fastsatt(sumPensjonPerUtbetaler)

                val bosattland =
                    skattemeldingUtgått
                        .forForekomsterAv(tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale)
                        .hvorMinstEnForekomst {
                            (bosattILandEtterSkatteavtale erLik "TH")
                        }

                hvis(
                    ikke(harInnsending) og ikke(harBegrensetSkatteplikt) og
                        bosattland og (pensjonGrunnlagMinusFastsatt erStørreEnn 0)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadTips)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harGlobalSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

        val harPostadresseIUtlandet = partInfo
           .forForekomsterAv { partInfo }
           .forForekomsterAv { postadresseIUtlandet }
           .hvorMinstEnForekomst{(landkode er oppgitt) og (landkode erUlik "NO")}

        val harTilleggsinfoOmBosattILandEtterSkatteavtale = skattemelding
            .forForekomsterAv { tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale }
            .hvorMinstEnForekomst { (bosattILandEtterSkatteavtale er oppgitt) }

        // skattepliktig har lagt til kort for avtalebosted under tema Andre forhold
        // gir bare utslag hvis skattepliktig er globalskattepliktig
        // og de har adresse "utland" i Folkeregisteret
        // og i skattemeldingen har primærbolig i Norge
        // og har registrert utleieinntekt for norsk primærbolig
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv { fastEiendom }
            .filtrer { formuesforekomster ->
                ((fastsatt(landkodeBeliggenhet) erLik "NO") eller (fastsatt(landkodeBeliggenhet) ikkeEr oppgitt)) og
                (
                   formuesforekomster.forForekomsterAv { fastEiendomSomFormuesobjekt }
                    .hvorMinstEnForekomst { fastsatt(boligbruk) erLik "primaerbolig" } eller (
                   formuesforekomster.forForekomsterAv { fastEiendomSomFormuesobjekt }
                    .forForekomsterAv { useksjonertBoenhet }
                    .hvorMinstEnForekomst { fastsatt(boligbruk) erLik "primaerbolig" }))
            }
            .forForekomsterAv{fastEiendomSomUtleieobjekt}
            .utslagFor {

                val harUtleieAvPrimaerbolig = hvorMinstEnForekomst { (nettoLeieinntektForAndel.beloep er oppgitt) eller
                                                                     (bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep er oppgitt) eller
                                                                     (bruttoLeieinntektVedSjablongbehandletUtleie.beloep er oppgitt)}


                hvis(
                    harGlobalSkatteplikt og
                            harTilleggsinfoOmBosattILandEtterSkatteavtale og
                            harPostadresseIUtlandet og
                            harUtleieAvPrimaerbolig
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
    }

