                hvis(ikke(harUtslagPaaKontroll) og
                     ((betaltSkattIUtlandetFormue + betaltSkattIUtlandetInntekt) erLik beloepMedMetodeDobbeltbeskatningKredit)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }
