sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {

      val harUtslagPaaKontroll = utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING")

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        //kontrollen gir utslag og merknad dersom skattyter har oppgitt betalt skatt i utlandet
        //og det ikke er oppgitt noe annet kort med samme land og dobbeltbeskatningsmetode = kredit
        //skal IKKE gi utslag hvis utslag på kontroll SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING
        //skal IKKE gi utslag dersom skatteplikt er begrenset
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
            .utslagFor {

                val landMedUtenlandskSkatt = verdiFor { landkode }

                val harKravOmKreditfradragForUtenlandskSkatt = hvorMinstEnForekomst {
                    (kravOmKreditfradragForUtenlandskFormuesskatt.beloepSomTall erStørreEnn 0) eller
                        (kravOmKreditfradragForUtenlandskInntektsskatt.beloepSomTall erStørreEnn 0) eller
                        (skattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall erStørreEnn 0) eller
                        (bruttoAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall erStørreEnn 0)
                }

                val harIngenInntektIEiendomskortPaaLandDetErBetaltSkattTilOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(fastEiendom)
                    .filtrer { fastsatt(landkodeBeliggenhet) erLik landMedUtenlandskSkatt }
                    .forForekomsterAv(gruppe(fastEiendomSomUtleieobjekt, fastEiendomSomRealisasjonsobjekt, fastEiendomSomFormuesobjekt))
                    .forAlleForekomster
                    .harIngenForekomsterDer { fastsatt(metodeVedDobbeltbeskatning) erLik "kredit" }

                val harIngenAndreKortPaaLandDetErBetaltSkattTilOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            arbeidsgiver,
                            pensjonsinntekt,
                            konto,
                            aksjeIkkeIAksjonaerregisteret,
                            aksjeIAksjonaerregisteret,
                            verdipapirfond,
                            obligasjonOgSertifikat,
                            aksjesparekonto,
                            annetFinansprodukt,
                            skattefavorisertIndividuellSparingTilPensjon,
                            annenKapitalinntektOgKostnad,
                            skattepliktigKundeutbytteUtenforVirksomhetPerUtbetaler,
                            virtuellEiendel,
                            deltakersAndelAvFormueOgInntekt
                        )
                    )
                    .filtrer { fastsatt(landkode) erLik landMedUtenlandskSkatt }
                    .forAlleForekomster
                    .harIngenForekomsterDer {
                        fastsatt(metodeVedDobbeltbeskatning) erLik "kredit"
                    }

                val harIngengrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil =
                    skattemeldingUtgått
                        .forForekomsterAv(grunnlagForKreditfradragFraNaeringsvirksomhetIUtlandet)
                        .harIngenForekomsterDer { fastsatt(landkode) erLik landMedUtenlandskSkatt }

                val harIkkeUtflyttingsskattMedLandDetErBetaltSkattTil =
                    skattemeldingUtgått
                        .forForekomsterAv(spesifikasjonAvUtflyttingOgUtflyttingsskatt)
                        .harIngenForekomsterDer { fastsatt(landkode) erLik landMedUtenlandskSkatt }

                val harIngenUtflyttingsskattMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt,
                            andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt,
                            opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt
                        )
                    )
                    .forForekomsterAv(gruppe(
                           realisasjonAvAksjeOgAnnetFinansprodukt,
                           realisasjonAvAndelISelskapMedDeltakerfastsetting,
                           realisasjonAvOpsjon))
                    .forAlleForekomster
                    .harIngenForekomsterDer {
                        fastsatt(metodeVedDobbeltbeskatning) erLik "kredit"
                    }

                hvis(
                    ikke(harUtslagPaaKontroll) og ikke(harBegrensetSkatteplikt) og
                        harKravOmKreditfradragForUtenlandskSkatt og
                        harIngenInntektIEiendomskortPaaLandDetErBetaltSkattTilOgMetodeVedDobbeltbeskatningOppgitt og
                        harIngenAndreKortPaaLandDetErBetaltSkattTilOgMetodeVedDobbeltbeskatningOppgitt og
                        harIngengrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil og
                        harIkkeUtflyttingsskattMedLandDetErBetaltSkattTil og
                        harIngenUtflyttingsskattMetodeVedDobbeltbeskatningOppgitt
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1, skjulerSkatteberegning)
                }
            }
        }
    }

