sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
       val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val finnesIkontrollisteMedForhaansutfyltEiendomssalg = finnesIKontrolliste(
                "Forhaandsutfylt eiendomssalg",
                listeAv(partsnr)
            )

        // Gir utslag hvis Skattepliktig ligger i kontrolliste
        // og har fått preutfylt salg av eiendom(realisasjon) i skattemeldingen
        skattemelding
            .forForekomsterAv{fastEiendom}
            .forForekomsterAv{fastEiendomSomRealisasjonsobjekt}
            .utslagFor {

                val harPreutfyltRealisasjonAvEiendom = hvorMinstEnForekomst{(grunnlag(gevinstVedRealisasjonAvFastEiendom.beloep) erStørreEnn 0) eller
                                                                            (grunnlag(tapVedRealisasjonAvFastEiendom.fradragsberettigetBeloep.beloep) erStørreEnn 0)}

                hvis(finnesIkontrollisteMedForhaansutfyltEiendomssalg og
                     harPreutfyltRealisasjonAvEiendom)
              {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val sumEierandelAlleForekomsterGrunnlag = skattemelding
            .forForekomsterAv{fastEiendom}
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .summer { grunnlag(eierandel) }

        val sumEierandelAlleForekomsterFastsatt = skattemelding
            .forForekomsterAv{fastEiendom}
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .summer { fastsatt(eierandel) }

        val diffSumEierandel = differanse(sumEierandelAlleForekomsterGrunnlag, sumEierandelAlleForekomsterFastsatt)

        // kontrollen gir utslag og tips dersom skattyter har redusert eierandel
        // og realisasjon av eiendom ikke er lagt inn
        // for å unngå at den gir utslag dersom det bare fjernet en forekomst som lå i utlast og lagt til en tilsvarende i fastsatt,
        //     legges det til et ekstrakriterie at denne kun skal gi utslag dersom det er diff i summen av eierandel på alle forekomster
        skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer{eiendomsforekomster ->
                eiendomsforekomster
                  .harIngenForekomsterDer{fastEiendomSomRealisasjonsobjekt er oppgitt}
            }
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .utslagFor {

            val harEndretEierandel = hvorMinstEnForekomst { grunnlag(eierandel) erStørreEnn fastsatt(eierandel) }

            hvis(harEndretEierandel og
                 (diffSumEierandel erStørreEnn 0)
                ) {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val boligforekomsterIUtkast = skattemelding
          .forForekomsterAv{fastEiendom}
          .filtrer{eiendomsforekomster ->
              eiendomsforekomster
                .forForekomsterAv{fastEiendomSomFormuesobjekt}
                .hvorMinstEnForekomst{grunnlag(eiendomstype).finnesBlant("selveidBolig", "boenhetIBoligselskap", "borett")}
          }
          .verdierFor{grunnlag(sergEiendomsidentifikator)}

        // kontrollen gir utslag på alle eiendommer hvor skattyter har endret eiendomstype fra bolig til fritidsbolig
        // skal kun gi utslag dersom formuesverdi er preutfylt på eiendommen
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{fastEiendom}
            .filtrer{fastsatt(sergEiendomsidentifikator).finnesBlant(boligforekomsterIUtkast)}
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .filtrer { grunnlag(formuesverdiPerEiendom.beloep) er oppgitt }
            .utslagFor {

              val harEndretFraBoligTilFritidsbolig = hvorMinstEnForekomst{fastsatt(eiendomstype).finnesBlant("egenFritaksbehandletFritidseiendom", "regnskapsbehandletFritidseiendom", "selveidFritidseiendom")}

              hvis(harEndretFraBoligTilFritidsbolig) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {

      // kontrollen gir utslag dersom SP legger til en eiendom som ikke var preutfylt
      // OG SP oppgir gevinst eller tap under "realisasjon" på denne eiendommen
      // OG de legger inn eierandel som er større enn 0 på denne eiendommen
      skattemelding
        .forForekomsterAv { fastEiendom }
        .filtrer{eiendomsforekomster ->
          ((grunnlag(sergEiendomsidentifikator) ikkeEr oppgitt) og
          (grunnlag(landkodeBeliggenhet) ikkeEr oppgitt) og
          ((fastsatt(sergEiendomsidentifikator) er oppgitt) eller (fastsatt(landkodeBeliggenhet) er oppgitt))) og
          ( eiendomsforekomster
             .hvorMinstEnForekomst{fastEiendomSomRealisasjonsobjekt er oppgitt}
          )
        }
        .forForekomsterAv{fastEiendomSomFormuesobjekt}
        .utslagFor {

          val harLagtTilEierandelPaaEgenfastsattEiendomMedGevinstEllerTap = hvorMinstEnForekomst{fastsatt(eierandel) erStørreEnn 0}

          hvis(harLagtTilEierandelPaaEgenfastsattEiendomMedGevinstEllerTap) {
            betjeningsstrategiSettesTil(
              ikke,
              merknadInformasjon
            )
          }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag på alle eiendommer av type boenhetIBoligselskap, selveidBolig eller UseksjonertBoenhetIFlerborligbygg
        // hvor skattyter har eierandel større enn 0
        // og som mangler areal, boligtype eller byggeår
        // kontrollen gir merknad i SME
        skattemelding
           .forForekomsterAv{fastEiendom}
           .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .utslagFor {

                val skattyterEierBoenhetIBoligselskapEllerSelveidBoligSomManglerArealByggeaarEllerBoligtype = hvorMinstEnForekomst{
                  (fastsatt(eiendomstype).finnesBlant("boenhetIBoligselskap", "selveidBolig")) og
                  (fastsatt(eierandel) erStørreEnn 0) og
                  ((fastsatt(boligensAreal) ikkeEr oppgitt) eller
                   (fastsatt(boligtype) ikkeEr oppgitt) eller
                   (fastsatt(byggeaar) ikkeEr oppgitt))
                }

                val skattyterEierUseksjonertBoenhetIFlerborligbyggSomManglerArealByggeaarEllerBoligtype = filtrer{(fastsatt(eiendomstype) erLik "flerboligbygning") og (fastsatt(eierandel) erStørreEnn 0)}
                   .forForekomsterAv{useksjonertBoenhet}
                   .hvorMinstEnForekomst{(fastsatt(boligensAreal) ikkeEr oppgitt) eller
                                         (fastsatt(boligtype) ikkeEr oppgitt) eller
                                         (fastsatt(byggeaar) ikkeEr oppgitt)
                   }

            hvis(skattyterEierBoenhetIBoligselskapEllerSelveidBoligSomManglerArealByggeaarEllerBoligtype eller skattyterEierUseksjonertBoenhetIFlerborligbyggSomManglerArealByggeaarEllerBoligtype
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadMangel
                )
            }
        }
        }

