        //feltet reduksjonsfaktor er oppgitt og
        //feltene dokumentert markedsverdi for tidligere inntektsår eller år for mottatt markedsverdi ikke er fylt ut og
        //feltet dokumentert markedsverdi er fylt ut

         skattemelding
              .utslagFor {

    val harReduksjonsfaktorForJustertMarkedsverdi  = forForekomsterAv{fastEiendom}
                  .forForekomsterAv{fastEiendomSomFormuesobjekt}
              .hvorMinstEnForekomst {
                      (fastsatt(reduksjonsfaktorForJustertMarkedsverdiForUtleidNaeringseiendom) erStørreEnn 0)

                  }

     val harIkkeMarkedsverdiTidligereAarEllerAarForMottattMarkedsverdi  = forForekomsterAv{fastEiendom}
                  .forForekomsterAv{fastEiendomSomFormuesobjekt}
                  .hvorMinstEnForekomst {
                 ((fastsatt(dokumentertMarkedsverdiForUtleidNaeringseiendomTidligereInntektsaar) er oppgitt) eller
                     (fastsatt(aarForMottattMarkedsverdiForUtleidNaeringseiendom) er oppgitt))
                  }

     val harDokumentertMarkedsverdi  = forForekomsterAv{fastEiendom}
                  .forForekomsterAv{fastEiendomSomFormuesobjekt}
                  .hvorMinstEnForekomst {
                 (fastsatt(dokumentertMarkedsverdiForUtleidNaeringseiendom.beloep) er oppgitt)
                  }

     hvis((harReduksjonsfaktorForJustertMarkedsverdi) og (ikke(harIkkeMarkedsverdiTidligereAarEllerAarForMottattMarkedsverdi) eller (harDokumentertMarkedsverdi)))
              {
                  betjeningsstrategiSettesTil(ikke, faktiskFeil)
              }
          }
     }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       // kontrollen gir utslag hvis skattyter har redusert p-rom (boligens areal) på eiendommer av type boenhetIBoligselskap, selveidBolig eller flerboligbygning
       skattemelding
          .forForekomsterAv{fastEiendom}
          .forForekomsterAv{fastEiendomSomFormuesobjekt}
                .utslagFor {

                   val skattyterEierBoenhetIBoligselskapEllerSelveidBoligOgHarRedusertProm = hvorMinstEnForekomst{
                     (fastsatt(eiendomstype).finnesBlant("boenhetIBoligselskap", "selveidBolig")) og
                     (grunnlag(boligensAreal) erStørreEnn 0) og
                     (fastsatt(boligensAreal) erMindreEnn (grunnlag(boligensAreal) * 90.prosent))
                }

                val skattyterEierUseksjonertBoenhetIFlerborligbyggOgHarRedusertProm = filtrer{(fastsatt(eiendomstype) erLik "flerboligbygning") og (fastsatt(eierandel) erStørreEnn 0)}
                   .forForekomsterAv{useksjonertBoenhet}
                   .hvorMinstEnForekomst{(grunnlag(boligensAreal) erStørreEnn 0) og
                                         (fastsatt(boligensAreal) erMindreEnn (grunnlag(boligensAreal) * 90.prosent))
                   }

                    hvis(skattyterEierBoenhetIBoligselskapEllerSelveidBoligOgHarRedusertProm eller
                         skattyterEierUseksjonertBoenhetIFlerborligbyggOgHarRedusertProm
                        ) {
                        betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                    }
                }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag og merknad dersom skattyter har en eiendom i Norge
        // og eiendommen Tilhører barn
        // og barnets fødselsnummer ikke er oppgitt - kun fødselsdato (må ha minst 11 tegn)
        skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer {(fastsatt(landkodeBeliggenhet) erLik "NO") eller (fastsatt(landkodeBeliggenhet) ikkeEr oppgitt)}
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .utslagFor {

                val harEiendomSomTilhoererBarnUtenFnr = hvorMinstEnForekomst {
                    (formuesverdiPerEiendom.beloep er oppgitt) og
                    (overfoertFraBarn.norskPersonidentifikator er oppgitt) og
                    (overfoertFraBarn.norskPersonidentifikator.formatert.lengde erUlik 11)
                }

                hvis(harEiendomSomTilhoererBarnUtenFnr) {
                    betjeningsstrategiSettesTil(ikke, merknadMangel, skjulerSkatteberegning)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) { 

        // Gir utslag på skattytere som har fastsatt minst en forekomst av utleie, formue eller realisasjon av eiendom 
        // og den faste eiendommmen ikke har hverken landkode eller kommunenummer
        skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer{((fastsatt(sergEiendomsidentifikator) er oppgitt) eller (fastsatt(poststedsnavnInternasjonalAdresse) er oppgitt)) og
                    (fastsatt(landkodeBeliggenhet) ikkeEr oppgitt) og
                    (fastsatt(kommunenummerBeliggenhet) ikkeEr oppgitt)}
            .utslagFor {

                val harEiendomMedFormueUtleieEllerRealisasjonUtenLandEllerKommune = hvorMinstEnForekomst{
                  (fastEiendomSomFormuesobjekt er oppgitt) eller
                  (fastEiendomSomUtleieobjekt er oppgitt) eller
                  (fastEiendomSomRealisasjonsobjekt er oppgitt)
                }


                hvis(harEiendomMedFormueUtleieEllerRealisasjonUtenLandEllerKommune) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning)
     {

        // Gir utslag på SP som legger til en eiendom med landkode som ikke er Norge
        // og eiendomstypen er noe annet enn en av de som er gyldige for utenlandske eiendommer
        //    borett
        //    egenFritaksbehandletBolig
        //    regnskapsbehandletBolig
        //    egenFritaksbehandletFritidseiendom
        //    regnskapsbehandletFritidseiendom
        //    tomt
        //    gaardsbruk
        //    skogeiendomIUtlandet
        //    ikkeUtleidNaeringseiendomIUtlandet
        //    annenFastEiendomInnenforInntektsgivendeAktivitet
        //    annenFastEiendomUtenforInntektsgivendeAktivitet
        //    utleieFlerboligbygningIUtlandet
        //    naeringseiendom (formuesgrunnlagUtleidNaeringseiendom)
        skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer{(grunnlag(poststedsnavnInternasjonalAdresse) ikkeEr oppgitt) og
                     (fastsatt(poststedsnavnInternasjonalAdresse) er oppgitt) og
                     (fastsatt(landkodeBeliggenhet) er oppgitt) og
                     (fastsatt(landkodeBeliggenhet) erUlik "NO")}
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .utslagFor {

                val harOppgittFeilTypeutenlandskEiendom = hvorMinstEnForekomst{fastsatt(eiendomstype).finnesBlant("boenhetIBoligselskap", "flerboligbygning", "ikkeUtleidNaeringseiendomINorge", "selveidBolig", "selveidFritidseiendom", "skogeiendomINorge")}


                hvis(harOppgittFeilTypeutenlandskEiendom) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

    // Gir utslag hvis summen av vedlikehold og andre kostnader > kr 150 000
       skattemelding
       .forForekomsterAv{fastEiendom}
       .forForekomsterAv{fastEiendomSomUtleieobjekt}
       .utslagFor {

       val sumUtleiekostnader =
           summer{fastsatt(vedlikeholdskostnadVedUtleie.beloep) +
                    fastsatt(annenKostnadVedUtleie.beloep)}

       val harVedleggVedlikehold =
           hvorMinstEnForekomst{vedlikeholdskostnadVedUtleie.beloep har vedleggPåFelt}

        val harVedleggAnnenKostnad =
           hvorMinstEnForekomst{annenKostnadVedUtleie.beloep har vedleggPåFelt}

       hvis(ikke(harVedleggVedlikehold) og ikke(harVedleggAnnenKostnad) og
             (sumUtleiekostnader erStørreEnn 150_000))
         {
                betjeningsstrategiSettesTil(ikke, merknadDokumentasjon)
            }
        }
        }

sakstype(Personlig)
