       skattemelding
       .forForekomsterAv{fastEiendom}
       .forForekomsterAv{fastEiendomSomUtleieobjekt}
       .utslagFor {

       val sumUtleiekostnader = summer{fastsatt(husleieFelleskostnadVedUtleie.beloep)}

       hvis(sumUtleiekostnader erStørreEnn 60_000)
         {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtvidetVeiledningPaaUtleie = utvidetVeiledning
           .forForekomsterAv { utvidetveiledning }
           .forForekomsterAv { metadata }
           .hvorMinstEnForekomst {type.finnesBlant("UTLEIE_AV_FAST_EIENDOM", "UTLEIE_SEKUNDAERBOLIGMODELL") }

         val harUtleieIFjoraaret = historiskSkattemelding(EttÅrTilbake)
             { skattemelding ->
                skattemelding
                 .hvorMinstEnForekomst {
                   (sjablongbehandletUtleie er oppgitt) eller
                   (sjablongbehandletUtleieIUtlandet er oppgitt) eller
                   (regnskapsbehandletUtleie er oppgitt) eller
                   (regnskapsbehandletUtleieIUtlandet er oppgitt) eller
                   (sjablongOgRegnskapsbehandletUtleie er oppgitt)}
             }

        // Kontrollen gir utslag dersom skattyter hadde utleie i fjor i Norge eller utlandet
        // og ikke har oppgitt noen utleie i år,
        // Skal ikke gi utslag hvis skattyter har fått utvidet veileding om utleie
        skattemelding
             .utslagFor {

             val harUtleieIAar = forForekomsterAv{fastEiendom}
                 .hvorMinstEnForekomst {fastEiendomSomUtleieobjekt er oppgitt }

             hvis(ikke(harUtvidetVeiledningPaaUtleie) og
                  harUtleieIFjoraaret og
                  ikke(harUtleieIAar)
             )
             {
                 betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
             }
         }
    }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

       // Kontrollen gir utslag dersom skattyter har oppgitt overskudd fra langtidsutleie på eiendom i Norge eller Utandet
       // OG det ikke er fylt inn noe bruttoleieinntekt eller fradrag for utgifter ved utleie
