                     (fastsatt(annenKostnadVedUtleie.beloep) erLik 0) og
                     (fastsatt(kommunalAvgift.beloep) erLik 0) og
                     (fastsatt(samletAvskrivning.beloep) erLik 0)
                 }


             hvis(harNettoleieinntektVedUtleieUtenBruttoinntektEllerKostnader)
             {
                 betjeningsstrategiSettesTil(ikke, faktiskFeil)
             }
         }
    }

