sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        val startdatoBosattIkkeMyndighetsfastsatt = skattemelding
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale}
            .filtrer{(fastsatt(bosattILandEtterSkatteavtale) ikkeEr myndighetsfastsatt) og
                     (fastsatt(startdatoForBosattIUtlandEtterSkatteavtale) ikkeEr myndighetsfastsatt) og
                     (fastsatt(sluttdatoForBosattIUtlandEtterSkatteavtale) ikkeEr myndighetsfastsatt) og
                     (fastsatt(betalerSkattTilBosattlandEtterSkatteavtale) ikkeEr myndighetsfastsatt) og
                     (fastsatt(oppfyllerIkkeVilkaarForBosattIUtlandEtterSkatteavtale) ikkeEr myndighetsfastsatt)}
            .verdiFor { startdatoForBosattIUtlandEtterSkatteavtale }

        val harOppgittStartdatoBosatt =  skattemelding
           .forForekomsterAv{tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale}
           .hvorMinstEnForekomst{fastsatt(startdatoForBosattIUtlandEtterSkatteavtale) er oppgitt}

        // kontrollen gir utslag dersom startdato for skattemessig emigrasjon er startdato for skattemessig bosatt i utlandet
        // skal ikke gi utslag hvis noen av feltene i kort for tilleggsinformasjonVedSkattemessigUtflytting eller tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale er myndighetsfastsatt
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigUtflytting}
            .utslagFor {

               val  harOppgittStartdatoForEmigrert = hvorMinstEnForekomst{startdatoForKravOmOpphoerAvSkattepliktTilNorge er oppgitt}    

               val startdatoEmigrertIkkeMyndighetsfastsatt = filtrer{(fastsatt(startdatoForKravOmOpphoerAvSkattepliktTilNorge) ikkeEr myndighetsfastsatt) og
                        (fastsatt(startdatoForFastOppholdIAnnetLand) ikkeEr myndighetsfastsatt) og
                        (fastsatt(antallOppholdsdagerINorgeErMindreEnn62Dager) ikkeEr myndighetsfastsatt) og
                        (fastsatt(harDisponertBoligINorgeEtterUtflytting) ikkeEr myndighetsfastsatt) og
                        (fastsatt(bosattILand) ikkeEr myndighetsfastsatt) og
                        (fastsatt(oppfyllerIkkeVilkaarForSkattemessigUtflytting) ikkeEr myndighetsfastsatt)}
                    .verdiFor {startdatoForKravOmOpphoerAvSkattepliktTilNorge}

                hvis(harOppgittStartdatoForEmigrert og
                     harOppgittStartdatoBosatt og
                    (startdatoEmigrertIkkeMyndighetsfastsatt er oppgitt) og
                     (startdatoBosattIkkeMyndighetsfastsatt er oppgitt) og
                    (startdatoEmigrertIkkeMyndighetsfastsatt erLik startdatoBosattIkkeMyndighetsfastsatt)
                ) {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

sakstype(Personlig)
        .kontekst(ValideringSkattepliktig)
    {
          val harBegrensetSkatteplikt = skattepliktGjeldende
                .forForekomsterAv{skatteplikt}
                .hvorMinstEnForekomst{personligSkattesubjekt.skattepliktTilNorge erLik "begrenset"}

            // kontrollen gir utslag dersom startdato for skattemessig emigrasjon(startdatoForKravOmOpphoerAvSkattepliktTilNorge)
            // er lik startdato for  fast opphold i annet land (startdatoForFastOppholdIAnnetLand)
            // skal ikke gi utslag hvis skattepliktig har skatteplikt Begrenset
            utgåtteSkattemeldingFelter {
                skattemeldingUtgått
                .fokusområde("Fokusområde")
                .forForekomsterAv(tilleggsinformasjonVedSkattemessigUtflytting)
                .utslagFor {
                    hvis(ikke(harBegrensetSkatteplikt) og
                        (startdatoForKravOmOpphoerAvSkattepliktTilNorge er oppgitt) og
                            (verdiFor { startdatoForKravOmOpphoerAvSkattepliktTilNorge }.dato erLik verdiFor { startdatoForFastOppholdIAnnetLand }.dato)
                    ) {
                        betjeningsstrategiSettesTil(ikke, merknadStandard)
                    }
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .importer(SLUTTDATO_I_INNTEKTSAARET)  {
