        skattemelding
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigUtflytting}
            .utslagFor {
                val startdatoEtterInntektsaaret = hvorMinstEnForekomst{(startdatoForKravOmOpphoerAvSkattepliktTilNorge er oppgitt) og
                                                                       (startdatoForKravOmOpphoerAvSkattepliktTilNorge erEtter sluttdatoIInntektsaaret)}

                hvis(startdatoEtterInntektsaaret) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

