       val startdatoIInntektsaaret = D_startdatoIInntektsaaret
       val sluttdatoIInntektsaaret = D_sluttdatoIInntektsaaret

        // kontrollen gir utslag dersom sluttbruker legger inn annet år enn inntektsåret i feltet "Fra hvilket tidspunkt krever du skattemessig emigrasjon"
        //i kortet "Krav om skattemessig emigrasjon"
        skattemelding
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigUtflytting}
            .utslagFor {
                val harOppgittdatoIkkeInntektsaaret = hvorMinstEnForekomst{(startdatoForKravOmOpphoerAvSkattepliktTilNorge er oppgitt) og
                                                                            ((startdatoForKravOmOpphoerAvSkattepliktTilNorge erFør startdatoIInntektsaaret) eller
                                                                             (startdatoForKravOmOpphoerAvSkattepliktTilNorge erEtter sluttdatoIInntektsaaret))}

                hvis(harOppgittdatoIkkeInntektsaaret) {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

