            .forForekomsterAv{partInfo}
            .hentSiste()
            .hvorMinstEnForekomst{utflyttingsdato.dato er oppgitt}

        // kontrollen gir utslag dersom skattyter har lagt til kort for skattemessig emigrasjon under tema Andre forhold, 
        // og har ikke utflyttingsdato i systemet Skatteplikt (må hentes fra Partinfo) 
        skattemelding
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigUtflytting}
            .utslagFor {

                val harOppgittInfoIKortForSkattemessigEmigrasjon = hvorMinstEnForekomst{
                  (startdatoForKravOmOpphoerAvSkattepliktTilNorge er oppgitt) eller
                  (startdatoForFastOppholdIAnnetLand er oppgitt) eller
                  (antallOppholdsdagerINorgeErMindreEnn62Dager er oppgitt) eller
                  (harDisponertBoligINorgeEtterUtflytting er oppgitt) eller
                  (bosattILand er oppgitt)}


                hvis(harOppgittInfoIKortForSkattemessigEmigrasjon og
                    ikke(harUtflyttingsdatoIPartinfo)
                ){
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon
                    )
                }
            }
