sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtslagPaaKontroll = utslagPåKontroll("EMIGRASJON_AVTALEBOSTED_SAMME_DATO")

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv{skatteplikt}
            .hvorMinstEnForekomst{personligSkattesubjekt.skattepliktTilNorge erLik "begrenset"}

        val harPensjonINorge = skattemelding
            .forForekomsterAv{pensjonsinntekt}
            .filtrer{(grunnlag(landkode) erLik "NO") eller (grunnlag(landkode) ikkeEr oppgitt)}
            .forForekomsterAv{perMaaned}
            .hvorMinstEnForekomst{grunnlag(pensjonsinntekt.beloep) erStørreEnn 0}

        // Skal gi utslag hvis skattepliktig legger inn krav om skattemessig emigrasjon under "Andre forhold" og
        //mottar pensjon utbetalt fra Norge eller
        //mottar uføreytelse utbetalt fra Norge
        // Skal ikke gi utslag hvis utslag på EMIGRASJON_AVTALEBOSTED_SAMME_DATO
        // skal ikke gi utslag hvis skatteplikt Begrenset
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigUtflytting}
            .utslagFor {

            val harOppgittTilleggsinfoOmSkattemessigUtflytting = hvorMinstEnForekomst{startdatoForKravOmOpphoerAvSkattepliktTilNorge er oppgitt}

            hvis(
                ikke(harUtslagPaaKontroll) og
                ikke(harBegrensetSkatteplikt) og
                    harOppgittTilleggsinfoOmSkattemessigUtflytting og
                    harPensjonINorge
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadInformasjon
                   )
              }
            }
        }

