sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtslagPaaKontroll = utslagPåKontroll("FAGFORENINGSKONTINGENT_IKKE_ARBEIDSINNTEKT")

        // kontrollen skal gi utslag og merknad dersom skattyter Skattepliktig IKKE har fagforeningskontingent i utkast, 
        // og egenfastsetter beløp for kontingent
        // skal ikke gi utslag dersom skattyter har utslag på FAGFORENINGSKONTINGENT_IKKE_ARBEIDSINNTEKT
        skattemelding
            .forForekomsterAv{fagforeningskontingent}
            .filtrer{grunnlag(samletBetaltFagforeningskontingentPrAar.beloep) erLik 0}
