                val differanseFormueVerdi = verdiFor{
                    grunnlag(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloep) -
                    fastsatt(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloep)}

                val differanseFormueKontanter = verdiFor{
                    grunnlag(formuesverdiForKontanterMv.beloep) -
                    fastsatt(formuesverdiForKontanterMv.beloep)}

                val formueOgKontanterErStoerreEnnBeloepsgrense = hvorMinstEnForekomst{(differanseFormueVerdi + differanseFormueKontanter) erStørreEnnEllerLik 500_000}

                hvis(ikke(harInnsending) og
                     formueOgKontanterErStoerreEnnBeloepsgrense
                    ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }

