sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag dersom skattyter har oppgitt kostnader til megler på over 51 000 + 4 % av salgssummen og større enn 100 000.
        // og Skattepliktig legger inn kostnader til megler over 1 000 000 kr.
        // Skal ikke gi utslag hvis solgt bolig er i utlandet/utenlandsk valuta.
        // kontrollen gir merknad i SME
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{fastEiendom}
            .filtrer{(fastsatt(landkodeBeliggenhet) erLik "NO") eller (fastsatt(landkodeBeliggenhet) ikkeEr oppgitt)}
            .forForekomsterAv{fastEiendomSomRealisasjonsobjekt}
            .utslagFor {

                val sumUtgifterTilMegler = summer{fastsatt(utgiftTilMegler.beloep)}

                val sumSalgssum = summer{fastsatt(salgssum.beloep)}

                val andelAvSalgssumPlussBeloepsgrense = (sumSalgssum * 4.prosent) + 51_000

            hvis((sumUtgifterTilMegler erStørreEnn 1_000_000) eller
                 ((sumUtgifterTilMegler - andelAvSalgssumPlussBeloepsgrense) erStørreEnn 100_000))
              {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
              }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

    val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer.sisteTegn }

    // Gir utslag hvis Skattepliktig legger inn beløp over 30 000 kr på fradragsfeltet for forsikring ved utleie av bolig.
    // ab test - gir utslag og merknad når siste siffer i partsnummeret er partall
       skattemelding
       .forForekomsterAv{fastEiendom}
       .forForekomsterAv{fastEiendomSomUtleieobjekt}
       .utslagFor {

       val sumForsikringspremie = summer{fastsatt(forsikringspremie.beloep)}

       hvis((partsnr.sisteTegn.finnesBlant("0", "2", "4", "6", "8")) og
            (sumForsikringspremie erStørreEnn 30_000))
         {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val skattepliktforekomster = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }

        val erIkkeSjoeEllerSokkel = skattepliktforekomster
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .harIngenForekomsterDer { (type erLik "skattepliktEtterPetroleumsskatteloven") eller (type erLik "skattepliktSomSjoemann") }

        val harBegrensetSkatteplikt = skattepliktforekomster
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val harFastDriftsted = periodelager
            .forForekomsterAv { perioder }
            .forForekomsterAv { arbeidsforholdsperiode }
            .hvorMinstEnForekomst { oppdrag.erFastDriftssted erLik true }

        // kontrollen gir utslag dersom skattyter har valgt "ikke skattepliktig" på lønnsinntekt
        // og skattyter har oppdrag med "Ja" på FD (fast driftssted) i OAR
        // og har begrenset skatteplikt (for å kunne slå opp i periodelager i SME)
        //  og IKKE har særskilt skatteplikt sjø og sokkel
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

            val harOppgittSkattefriLoenn = forForekomsterAv{arbeidsgiver}
                .forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                .hvorMinstEnForekomst {
                    (
                        fastsatt(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattefriMedTrygdeavgiftsplikt",
                            "skattefriKildeskattPaaPensjon",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                        )
                        ) og
                        (
                            ikke(
                                grunnlag(beloep.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                    beloep.trygdeEllerSkattemessigUnntak
                                )
                            )
                            )
                }


            hvis(harBegrensetSkatteplikt og
                 erIkkeSjoeEllerSokkel og
                 harOppgittSkattefriLoenn og
                 harFastDriftsted) {
                betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val skattepliktforekomster = skattepliktGjeldende
          .forForekomsterAv { skatteplikt }

        val harBegrensetSkatteplikt = skattepliktforekomster
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val periodelagerforekomster = periodelager

        val harOppholOver183Dager = periodelagerforekomster
            .forForekomsterAv { totalPerioder }
            .hvorMinstEnForekomst {
                (antallDagerMedArbeidsforholdsperioder erStørreEnn 183)
            }

        val oppdragErInnleie = periodelagerforekomster
            .forForekomsterAv { perioder }
            .forForekomsterAv { arbeidsforholdsperiode }
            .hvorMinstEnForekomst { oppdrag.kontraktstype erLik "innleieAvOppdragstaker" }

        val harFastDriftsted = periodelager
            .forForekomsterAv { perioder }
            .forForekomsterAv { arbeidsforholdsperiode }
            .hvorMinstEnForekomst { oppdrag.erFastDriftssted erLik true }

        val harOrdinaerTrekkfritakstype = periodelager
            .forForekomsterAv { perioder }
            .forForekomsterAv { trekkfritaksperiode }
            .hvorMinstEnForekomst { trekkfritakstype erUlik "nt1" }

        // kontrollen gir utslag dersom skattyter har valgt "ikke skattepliktig" på lønnsinntekt,
        // og skattyter har begrenset skatteplikt
        // og har trekkfritakstype ordinær (ulik NT1)
        // og  har "Innleie", "Fast driftsted" eller Arbeidsforhold i OAR er over 183 dager i 12 måneders periode i OAR
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val harOppgittSkattefriLoenn = forForekomsterAv(arbeidsgiver)
                .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                .hvorMinstEnForekomst {
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                    )) og
                        (ikke(
                            grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                beloepSomGruppe.trygdeEllerSkattemessigUnntak
                            )
                        ))
                }

            hvis(
                harBegrensetSkatteplikt og harOrdinaerTrekkfritakstype og
                    (harOppholOver183Dager eller oppdragErInnleie eller harFastDriftsted) og
                    harOppgittSkattefriLoenn
            ) {
                betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
            }
        }
        }
    }

