sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        // kontrollen gir utslag dersom skattepliktig har grunnlagsdata på kostnader(renter) fra borettslag/sameie, og skattepliktig øker beløpet.
        // AB-test: Gir utslag og merknad på skattepliktige som har følgende i siste siffer i partsnummeret: 0, 2, 4, 6, 8
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{grupper(andelAvInntektFelleskostnaderFormueOgGjeldIBoligsameie,
                                      andelAvInntektFelleskostnaderFormueOgGjeldIBoligselskap)}
            .utslagFor {
                val harOektKostnaderTilBorettslagEllerSameie = hvorMinstEnForekomst{
                  (grunnlag(andelAvFradragsberettigedeRenter.fradragsberettigetBeloep.beloep) er oppgitt) og
                  ((fastsatt(andelAvFradragsberettigedeRenter.fradragsberettigetBeloep.beloep) -
                    grunnlag(andelAvFradragsberettigedeRenter.fradragsberettigetBeloep.beloep)) erStørreEnn 0)}

                hvis((partsnr.sisteTegn.finnesBlant("0", "2", "4", "6", "8")) og
                      harOektKostnaderTilBorettslagEllerSameie) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadBekreft, avhukingsboks

                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val kortArbeidsoppholdUtenforHjemmet = skattemelding
          .forForekomsterAv{arbeidsoppholdUtenforHjemmet}

        // Kontrollen gir utslag og merknad når skattyter har lagt til andre kostnader knyttet til arbeidsinntekt
        // Skattepliktig har beskrivelse som inneholder ord fra liste, eller har nøyaktig ord fra liste
        // Skattepliktig krever > 0 i feltet "Andre kostnader knyttet til arbeidsinntekt"
        // og andre kostnader knyttet til arbeidsinntekt som er med og overstyrer minstefradrag > minstefradrag
        skattemelding
            .forForekomsterAv{minstefradragOgKostnaderKnyttetTilArbeidOgAnnenInntekt}
            .utslagFor {

                val sumAndreKostnaderArbeidsinntektStørreEnnMinstefradrag = summer{
                  fastsatt(fradragForKostnaderKnyttetTilArbeid.beloepUtenHensynTilValgtPrioritertFradragstype.beloep) -
                  fastsatt(minstefradragIInntekt.beloepUtenHensynTilValgtPrioritertFradragstype.beloep)}

                val harBeskrivelseSomInneholderOrdeUnderArbeidsinntekt = forForekomsterAv{kostnad}
                    .hvorMinstEnForekomst {
                        (fastsatt(kostnadstype) erLik "andreKostnaderKnyttetTilArbeidsinntekt") og
                        (beloep.beloep erStørreEnn 0) og
                        ((beskrivelse.inneholder("overnatt")) eller
                         (beskrivelse.inneholder("pendle")) eller
                         (beskrivelse.inneholder("merutgift")) eller
                         (beskrivelse.inneholder("merkostnad")) eller
                         (beskrivelse.inneholder("langtransport")) eller
                         (beskrivelse.inneholder("yrkessjåfør")) eller
                         (beskrivelse.inneholder("hybel")) eller
                         (beskrivelse.inneholder("hotel")) eller
                         (beskrivelse.inneholder("lastebil"))
                        )
                    }

                val harBeskrivelseMedNøyaktigOrdUnderArbeidsinntekt = forForekomsterAv{kostnad}
                    .hvorMinstEnForekomst {
                        (fastsatt(kostnadstype) erLik "andreKostnaderKnyttetTilArbeidsinntekt") og
                        (beloep.beloep erStørreEnn 0) og
                        ((beskrivelse.erLik("kostpenger")) eller
                         (beskrivelse.erLik("accomodation")) eller
                         (beskrivelse.erLik("rent")) eller
                         (beskrivelse.erLik("housing")) eller
                         (beskrivelse.erLik("losji")) eller
                         (beskrivelse.erLik("bokostnader")) eller
                         (beskrivelse.erLik("kosttrekk")) eller
                         (beskrivelse.erLik("brakke")) eller
                         (beskrivelse.erLik("campingvogn"))
                        )
                    }

                hvis((sumAndreKostnaderArbeidsinntektStørreEnnMinstefradrag erStørreEnn 0) og
