sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
     .importer(SKATTEFRIE_UNNTAK)
     {
       (D_skattefrieUnntak) ->

        val erKPPOgHarSatsEtterAvtale = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { (type erLik "kildeskattepliktPaaPensjon") og
                                    (satsEtterSkatteavtale er oppgitt) og
                                    (satsEtterSkatteavtale.finnesIkkeBlant("0", "15"))}

        // Gir utslag på skattytere som er KPP
        // og Sats etter skatteavtale er oppgitt i skatteplikt (0-14) (skal ikke slå ut når sats i skatteplikt er lik 0 eller 15)
        // og ikke ALL pensjon har skattemessig unntak (gir utslag på pensjon som ikke er skattefri)
        skattemelding
            .forForekomsterAv{pensjonsinntekt}
            .utslagFor {

                val harSkattepliktigPensjon = forForekomsterAv{perMaaned}
                    .hvorMinstEnForekomst {
                        (fastsatt(pensjonsinntekt.beloep) erStørreEnn 0) og
                        (fastsatt(pensjonsinntekt.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(D_skattefrieUnntak))
                    }

                hvis(erKPPOgHarSatsEtterAvtale og
                     harSkattepliktigPensjon) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard
                    )
                }
            }
        }

