            .hvorMinstEnForekomst { type erLik "kildeskattepliktPaaPensjon" }

        val harOppgittLandITilleggsinfoOmKPPIfjor = historiskSkattemelding(EttÅrTilbake)
       { skattemelding ->
           skattemelding
             .forForekomsterAv{tilleggsinformasjonVedKildeskattPaaPensjon}
             .hvorMinstEnForekomst{fastsatt(bosattILandEtterSkatteavtale) er oppgitt}
       }

        val landITilleggsinfoOmKPPIFjor = historiskSkattemelding(EttÅrTilbake)
       { skattemelding ->
           skattemelding
             .forForekomsterAv{tilleggsinformasjonVedKildeskattPaaPensjon}
             .verdiFor { fastsatt(bosattILandEtterSkatteavtale) }
       }

        // Gir utslag på skattytere som er KPP
        // og skattyter har lagt til annet land i tilleggsinfo om KPP under Andre forhold i år enn i fjor
        // Skal bare gi utslag dersom skattepliktig oppga bosattland i tilleggsinformasjonen i fjor
        // og skattyter IKKE har lagt ved vedlegg
        skattemelding
            .forForekomsterAv{tilleggsinformasjonVedKildeskattPaaPensjon}
            .utslagFor {

                val harOppgittLandIAarUliktLandIFjor = hvorMinstEnForekomst{(fastsatt(bosattILandEtterSkatteavtale) er oppgitt) og
                                                                            (fastsatt(bosattILandEtterSkatteavtale) erUlik landITilleggsinfoOmKPPIFjor)}

                val harLagtVedVedleggPaaBosattland = hvorMinstEnForekomst{fastsatt(bosattILandEtterSkatteavtale) har vedleggPåFelt}

                hvis(erKPP og
                    harOppgittLandITilleggsinfoOmKPPIfjor og
                    harOppgittLandIAarUliktLandIFjor og
                    ikke(harLagtVedVedleggPaaBosattland)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon
                    )
                }
            }
        }

