sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val erKPPOgHarSatsEtterAvtaleOgSkalBeskattesSomBosattISkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { (type erLik "kildeskattepliktPaaPensjon") og
                                    (skalBeskattesSomBosatt erLik true)}

        // Gir utslag på skattytere som er KPP
        // og skalBeskattesSomBosatt i skatteplikt  = Ja (true)
        // Skal bare gi utslag dersom skattyter ikke har krysset av for "Jeg krever å bli skatteberegnet som bosatt i Norge"
        // Denne avkrysningen lagres ikke i skattemeldingen, må derfor sjekke på om endringsårsak = kildeskattPensjonKreverBeskattetSomBosatt (denne settes automatisk ved avkryssning)

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { fastsatt(saerskiltSkatteplikt) erLik "kildeskattepliktPaaPensjon" }
            .utslagFor {

                val harPensjon = fastsatt(pensjonsinntektPrSkattleggingsperiode.beloepSomTall) erStørreEnn 0

                val harIkkeKrevdAaBliSkatteberegnetSomBosattINorge = forForekomsterAv(aarsakTilEndring)
                    .harIngenForekomsterDer { fastsatt(aarsak) erLik "kildeskattPensjonKreverBeskattetSomBosatt" }




                hvis(erKPPOgHarSatsEtterAvtaleOgSkalBeskattesSomBosattISkatteplikt og
                     harPensjon og
                     harIkkeKrevdAaBliSkatteberegnetSomBosattINorge) {
                    betjeningsstrategiSettesTil(
