sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtslagPaaKontroll = utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING")

        val harKrevdKreditfradragPaaFormueEllerInntekt = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forAlleForekomster
            .hvorMinstEnForekomst { (fastsatt(metodeVedDobbeltbeskatning) erLik "kredit") }
        }

        val beloepMedMetodeDobbeltbeskatningKredit = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forAlleForekomster
            .filtrer { (fastsatt(metodeVedDobbeltbeskatning) erLik "kredit") }
            .summer { fastsatt(beloepSomTall) }
        }

        val harVedleggAvTypeBetalingSkattIUtlandet = skattemelding
            .forForekomsterAv{vedlegg}
            .hvorMinstEnForekomst { vedleggstype erLik "betalingSkattIUtlandet" }

        //Kontrollen gir utslag på skattepliktig som i kort for kreditfradrag oppgir at betalt skatt i land X er 50 % (eller mer) av inntekt/formue opptjent i land X.
        //    Skal også gi utslag på tilfeller hvor betalt skatt i land X er mer enn 100 % av inntekt opptjent i land X. F.eks. inntekt opptjent i Belgia er NOK 100 000, men betalt skatt i Belgia er oppgitt å være NOK 80 000. Eventuelt NOK 200 000.
        // må ha minst en forekomst av "Kreditfradrag" i felt "Metode ved dobbeltbeskatning" i et kort med inntekt/formue.
        // må ha minst en forekomst av betalt Inntektsskatt i utlandet i kort i kreditfradrag (uavhengig av betalt formuesskatt)
        //og har ikke:
        //    (vedlegg med vedleggskategori = 'betalingSkattIUtlandet'
        //     eller har vedlegg på felt kravOmKreditfradragForUtenlandskFormuesskatt
        //     eller har vedlegg på felt kravOmKreditfradragForUtenlandskInntektsskatt
        // og har ikke felt "brutto aksjeutbytte" og "herav skatt på brutto aksjeutbytte"
        //skal ikke gi utslag hvis utslag på faktiskfeil SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{kravOmKreditfradragForUtenlandskSkatt}
            .utslagFor {

                val harOppgittBruttoAksjeutbytteEllerSkattPaaBruttoAksjeutbytte = hvorMinstEnForekomst{
                    (fastsatt(skattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap.beloep) er oppgitt) eller
                    (fastsatt(bruttoAksjeutbytteMottattFraUtenlandskSelskap.beloep) er oppgitt)
                }

                val betaltSkattIUtlandetFormueUtenVedlegg = filtrer{fastsatt(kravOmKreditfradragForUtenlandskFormuesskatt.beloepIValuta) harIkke vedleggPåFelt}
                    .summer { fastsatt(kravOmKreditfradragForUtenlandskFormuesskatt.beloep) }

                val betaltSkattIUtlandetInntektUtenVedlegg = filtrer{fastsatt(kravOmKreditfradragForUtenlandskInntektsskatt.beloepIValuta) harIkke vedleggPåFelt}
                    .summer { fastsatt(kravOmKreditfradragForUtenlandskInntektsskatt.beloep) }

                val sumBetaltSkattIUtlandetUtenVedlegg = betaltSkattIUtlandetFormueUtenVedlegg + betaltSkattIUtlandetInntektUtenVedlegg

                val landMedBetaltSkatt = verdierFor { landkode }

                hvis(ikke(harOppgittBruttoAksjeutbytteEllerSkattPaaBruttoAksjeutbytte) og
                     ikke(harUtslagPaaKontroll) og
                     ikke(harVedleggAvTypeBetalingSkattIUtlandet) og
                     harKrevdKreditfradragPaaFormueEllerInntekt og
                     (sumBetaltSkattIUtlandetUtenVedlegg erStørreEnn 0) og
                     (sumBetaltSkattIUtlandetUtenVedlegg erStørreEnnEllerLik (beloepMedMetodeDobbeltbeskatningKredit * 50.prosent))
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val harUtslagPaaKontroll = utslagPåKontroll("BETALT_SKATT_I_UTLANDET_MANGLER_LAND_ELLER_METODE") eller
                                   utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING")

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv{skatteplikt}
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val kreditfradragFormue = beregnetSkattDokument
            .summer { beregnetSkatt.skattefradrag.skattefradragForFormuesskattBetaltIUtlandet }

        val kreditfradragInntekt = beregnetSkattDokument
            .summer { beregnetSkatt.skattefradrag.skattefradragForInntektsskattBetaltIUtlandet }

        //kontrollen gir utslag og merknad dersom oppgitt betalt skatt i utlandet på formue eller inntekt overstiger kreditfradraget for formue eller inntekt som skattyter får
        //og det er registrert minst et objektkort med samme land og dobbeltbeskatningsmetode = kredit
        //skal ikke gi utslag hvis utslag på annen kontroll
        //skal ikke gi utslag hvis skattyter har begrenset skatteplikt
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
            .utslagForFørste {

                val landMedUtenlandskSkatt = verdierFor { landkode }

                val betaltSkattIUtlandetFormue =
                    summer { kravOmKreditfradragForUtenlandskFormuesskatt.beloepSomTall }

                val betaltSkattIUtlandetInntekt =
                    summer { kravOmKreditfradragForUtenlandskInntektsskatt.beloepSomTall }

                val harUtenlandskEiendomskortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(fastEiendom)
                    .filtrer { fastsatt(landkodeBeliggenhet).finnesBlant(landMedUtenlandskSkatt) }
                    .forForekomsterAv(
                        gruppe(fastEiendomSomFormuesobjekt, fastEiendomSomUtleieobjekt, fastEiendomSomRealisasjonsobjekt)
                    )
                    .forAlleForekomster
                    .hvorMinstEnForekomst { fastsatt(metodeVedDobbeltbeskatning) erLik "kredit" }

                val harAndreKortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            arbeidsgiver,
                            pensjonsinntekt,
                            konto,
                            aksjeIkkeIAksjonaerregisteret,
                            aksjeIAksjonaerregisteret,
                            verdipapirfond,
                            obligasjonOgSertifikat,
                            aksjesparekonto,
                            annetFinansprodukt,
                            skattefavorisertIndividuellSparingTilPensjon,
                            annenKapitalinntektOgKostnad,
                            skattepliktigKundeutbytteUtenforVirksomhetPerUtbetaler,
                            virtuellEiendel,
                            deltakersAndelAvFormueOgInntekt
                        )
                    )
                    .filtrer { fastsatt(landkode).finnesBlant(landMedUtenlandskSkatt) }
                    .forAlleForekomster
                    .hvorMinstEnForekomst {
                        fastsatt(metodeVedDobbeltbeskatning) erLik "kredit"
                    }

                val harGrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil =
                    skattemeldingUtgått
                        .forForekomsterAv(grunnlagForKreditfradragFraNaeringsvirksomhetIUtlandet)
                        .hvorMinstEnForekomst { fastsatt(landkode).finnesBlant(landMedUtenlandskSkatt) }


