                                 utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_MERKNAD_TIPS") eller
                                 utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_IKKE_MERKNAD")

        // Kontrollen gir utslag dersom skattepliktig har bankinnskudd i grunnlag hvor bankens orgnummer er 919932465
         // AB-test: skattytere som har tallet 1,2,3,4,5,6,7 eller 8 i siste siffer i partsnummeret får utslag
        // kontrollen skal ikke gi utslag dersom det er utslag på gitte kontroller
       skattemelding
          .fokusområde("Fokusområde")
          .forForekomsterAv{konto}
          .utslagFor {

          val harBankinnskuddPaaOrgnr = hvorMinstEnForekomst{
             (grunnlag(organisasjonsnummer.organisasjonsnummer) erLik "919932465") og
             (grunnlag(innskudd.beloep) erStørreEnn 0) }

         hvis(ikke(harUtslagPaaKontroll) og
              harBankinnskuddPaaOrgnr og
             (partsnr.sisteTegn.finnesBlant("1","2","3","4","5","6","7","8")))
                {
                    betjeningsstrategiSettesTil(ikke, merknadTips)
                }
            }
        }

