      val harUtslagPaaKontroll = utslagPåKontroll("MANGE_ARBEIDSREISER_ARBEIDET_SEKS_MÅNEDER") eller
                                 utslagPåKontroll("MANGE_ARBEIDSREISER_ARBEIDET_SEKS_MÅNEDER_IKKE_MERKNAD") eller
                                 utslagPåKontroll("ARBEIDSREISE_DAG_SYKEPENGER") eller
                                 utslagPåKontroll("REISEFRADRAG_UTEN_LØNN")

      // kontrollen gir utslag dersom skattyter har arbeidsreise med 231 dager eller mer - gir utslag pr arbeidsreisene
      // skal ikke gi utslag dersom den har utslag på andre arbeidsreisekontroller
      // ab test - gir utslag og merknad når siste siffer i partsnummeret er partall
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{reiserIArbeidsforhold}
            .forForekomsterAv{arbeidsreise}
            .utslagFor {
                val antallReisedagerPrArbeidsreise = summer { antallReiser }

                hvis(ikke(harUtslagPaaKontroll) og
                     (partsnr.sisteTegn.finnesBlant("0", "2", "4", "6", "8")) og
                     (antallReisedagerPrArbeidsreise erStørreEnnEllerLik 231)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

