     .importer(STARTDATO_I_INNTEKTSAARET) {
     (D_startdatoIInntektsaaret) ->

       val startdatoIInntektsaaret = D_startdatoIInntektsaaret

        //kontrollen gir utslag dersom skattyter har svar ja på mottatt fradrag de 2 foregående år, og skattyter IKKE har oppgitt pendlerbolig med startdato før inntektsåret
        skattemelding
            .forForekomsterAv{pendleropphold}
            .utslagFor {
                val pendleroppholdFoerInntektsaaret = forForekomsterAv{spesifikasjonAvPendlerbolig}
                    .hvorMinstEnForekomst {startdato erFør startdatoIInntektsaaret}

                val harMottattFradrag2ForegaaendeAar = hvorMinstEnForekomst{mottokFradragForPendleroppholdILoepetAvToForegaaendeInntektsaar erLik true}

                hvis(harMottattFradrag2ForegaaendeAar og
                     ikke(pendleroppholdFoerInntektsaaret)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadMangel)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .importer(STARTDATO_I_INNTEKTSAARET) {
     (D_startdatoIInntektsaaret) ->

       val startdatoIInntektsaaret = D_startdatoIInntektsaaret

       val haddePendleroppholdIFjor = fjoraaretsSkattemelding
            .forForekomsterAv { pendleropphold }
            .hvorMinstEnForekomst {
                fastsatt(beregnetKostnadForKostOgLosjiForPendleropphold.beloep) erStørreEnn 0
            }

        val haddeFradragForMerkostnaderIFjor = fjoraaretsSkattemelding
            .forForekomsterAv { arbeidsoppholdUtenforHjemmet }
            .hvorMinstEnForekomst {
                fastsatt(fradragForMerkostnadVedArbeidsoppholdUtenforHjemmet.fradragsberettigetBeloep.beloep) erStørreEnn 0
            }

        // kontrollen gir utslag dersom skattyter har svar Nei på mottatt fradrag de 2 foregående år,
        // og skattyter IKKE har oppgitt pendlerbolig med startdato før inntektsåret
        // og skattyter hadde pendleropphold og fradrag for merkostnader i fjor
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pendleropphold)
            .utslagFor {
                val pendleroppholdFoerInntektsaaret = forForekomsterAv(spesifikasjonAvPendlerbolig)
                    .hvorMinstEnForekomst {startdato erFør startdatoIInntektsaaret}
                hvis(
                    (mottokFradragForPendleroppholdILoepetAvToForegaaendeInntektsaar erLik false) og
                        ikke(pendleroppholdFoerInntektsaaret) og
                        haddePendleroppholdIFjor og
                        haddeFradragForMerkostnaderIFjor
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadMangel)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .importer(SLUTTDATO_I_INNTEKTSAARET)  {
      (D_sluttdatoIInntektsaaret) ->

      val defaultSluttPendlerboligNaarIkkeOppgitt = D_sluttdatoIInntektsaaret
      val defaultSluttArbeidsstedNaarIkkeOppgitt = D_sluttdatoIInntektsaaret

      // kontrollen gir utslag og merknad dersom skattyter ikke har arbeidssted i samme periode som oppgitt pendlerbolig
      // må også hensynta dersom skattyter ikke har oppgitt sluttdato for arbeidssted og/eller pendlerbolig
      skattemelding
      .fokusområde("Fokusområde")
        .forForekomsterAv { pendleropphold }
        .forForekomsterAv { spesifikasjonAvPendlerbolig }
        .utslagFor {

          val startPendlerbolig = verdiFor { startdato }
          val sluttPendlerbolig = verdiForEllerDefault(defaultSluttPendlerboligNaarIkkeOppgitt) { sluttdato }

          val startDatoErOppgitt =
          hvorMinstEnForekomst { startdato er oppgitt }

          val harIkkeArbeidsstedISammePeriode = skattemelding
          .forForekomsterAv { pendleropphold }
            .forForekomsterAv { spesifikasjonAvArbeidssted }
            .harIngenForekomsterDer { forekomst ->
              val startArbeidssted = forekomst.verdiFor { startdato }
              val sluttArbeidssted = forekomst.verdiForEllerDefault(defaultSluttArbeidsstedNaarIkkeOppgitt) { sluttdato }
              val harIkkeArbeidsstedISammePeriodeSomPendlerboligMedSluttdatoArbeidsstedOgSluttdatoPendlerbolig =
              ((startPendlerbolig erEtterEllerLik startArbeidssted) og (startArbeidssted erFørEllerLik sluttPendlerbolig)) og
              ((sluttArbeidssted erEtterEllerLik startPendlerbolig) og (sluttPendlerbolig erFørEllerLik  sluttArbeidssted))

              harIkkeArbeidsstedISammePeriodeSomPendlerboligMedSluttdatoArbeidsstedOgSluttdatoPendlerbolig
            }

          hvis( startDatoErOppgitt og harIkkeArbeidsstedISammePeriode) {
            betjeningsstrategiSettesTil(ikke, merknadMangel)
          }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {
        val antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(gruppe(oppholdUnderYrkesOgTjenestereise, oppholdUnderKjoereoppdrag))
            .summer { antallOvernattingsdoegn }
        }

        val antallOvernattingsdøgnPendleropphold = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(pendleropphold, spesifikasjonAvPendlerbolig))
            .summer { antallOvernattingsdoegn }
        }

        //kontrollen gir utslag dersom skattyter har merkostnader for mer enn 365 overnattingsdøgn
        //Gir merknadStandard
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    sti(pendleropphold, spesifikasjonAvPendlerbolig),
                    oppholdUnderYrkesOgTjenestereise,
                    oppholdUnderKjoereoppdrag
                )
            )
            .utslagFor {
                val harAntallReiser = hvorMinstEnForekomst { antallOvernattingsdoegn er oppgitt }

                hvis(
                    harAntallReiser og
                        ((antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag + antallOvernattingsdøgnPendleropphold) erStørreEnn 365)
                )

                {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val pendleroppholdene = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(
            sti(
                pendleropphold,
                spesifikasjonAvPendlerbolig
            )
        )
        }

        val overlappendePendleropphold = utgåtteSkattemeldingFelter {
            pendleroppholdene.hvor {
                val start = verdiFor { startdato }.dato
                val slutt = verdiFor { sluttdato }.dato
                val overlapperMedAndreOpphold = pendleroppholdene.filtrer {
                    val start2 = verdiFor { startdato }.dato
                    val slutt2 = verdiFor { sluttdato }.dato
                    (
                        (start2 erStørreEnnEllerLik start) og (start2 erMindreEnn slutt)
                        ) eller (
                        (slutt2 erStørreEnn start) og (slutt2 erMindreEnnEllerLik slutt)
                        )
                }
                overlapperMedAndreOpphold.antall erStørreEnn 1
            }
        }

        // kontrollen gir utslag dersom det er registrert flere pendleropphold på samme periode
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(pendleropphold)
            .utslagFor {
                val harPendleropphold = forForekomsterAv(spesifikasjonAvPendlerbolig)
                    .hvorMinstEnForekomst { fastsatt(startdato) er oppgitt }

                hvis(harPendleropphold og overlappendePendleropphold) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {

        val harUtslagPaaKontrollForYrkesopphold = utslagPåKontroll("YRKESOPPHOLD_I_HJEMKOMMUNE") eller
                                                  utslagPåKontroll("YRKESOPPHOLD_I_HJEMKOMMUNE_IKKE_MERKNAD")

        val godtgjoerelseFraarbeidsgiver = skattemelding
            .forForekomsterAv{arbeidsgiver}
            .forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
            .hvorMinstEnForekomst {
                behandlingsart.finnesBlant(
                    "BOLIG_UTL_PENDLER", "DIETT_HOTELL",
                    "DIETT_LANGTR_UTL", "DIETT_HYBEL",
                    "DIETT_PENSJONAT", "FRI_KO_LO_BO_PENDLER",
                    "FRI_KOST_LOSJI", "GODTGJ_EOS_PENDLER",
                    "KOSTBESPARELSE", "LOSJI_BRAKKE",
                    "TFRI_KOST_BESOK", "TFRI_KOST_O_28DG",
                    "TFRI_KOST_O_28DG_UT", "TFRI_KOST_TJENESTE",
                    "UTGGJ_REISE_KOST_LOSJI"
                )
            }

        //kontrollen gir utslag og merknad dersom skattyter har godtgjørelser fra arbeidsgiver
        //og har merkostnad hvor antall døgn er større enn 0
        //og det ikke er oppgitt at merkostnad er dekket av arbeidsgiver(pendleropphold)
        //    eller mottarKostgodtgjoerelseLavereEnnSats (oppholdUnderKjoereoppdrag og oppholdUnderYrkesOgTjenestereise)
        //merknaden skal kun vises på merkostnadskortet og ikke på lønnskortene
        //Skal ikke komme opp på kort for yrkesopphold hvis det er utslag på en av følgende kontroller
        //    YRKESOPPHOLD_I_HJEMKOMMUNE
        //    YRKESOPPHOLD_I_HJEMKOMMUNE_IKKE_MERKNAD
        //    Hvis parten har krevd fradrag for pendling og/eller kjøreopphold i tillegg, kan MERKOSTNAD_M_GODTGJØRELSE komme opp på kort for pendleropphold/kjøreopphold.
        skattemelding
                .fokusområde("Fokusområde")
                .forForekomsterAv{
                    grupper(
                        pendleropphold,
                        oppholdUnderKjoereoppdrag,
                        oppholdUnderYrkesOgTjenestereise
                    )
                }
                .utslagFor {

                    val harOppholdUnderYrkesOgTjenestereise = skattemelding
                       .hvorMinstEnForekomst{oppholdUnderYrkesOgTjenestereise er oppgitt}

                    val harPendleropphold = skattemelding
                       .hvorMinstEnForekomst{pendleropphold er oppgitt}

                    val harOppholdUnderKjoereoppdrag = skattemelding
                       .hvorMinstEnForekomst{oppholdUnderKjoereoppdrag er oppgitt}

                    val merkostnaderUtenGodtgjørelseLavereEnnSats = skattemelding
                       .forForekomsterAv{grupper(oppholdUnderYrkesOgTjenestereise, oppholdUnderKjoereoppdrag)}
                       .hvorMinstEnForekomst {(fastsatt(antallOvernattingsdoegn) erStørreEnn 0) og
                                              (fastsatt(mottarKostgodtgjoerelseLavereEnnSats) erLik false)
                       }

                    val merkostnaderUtenOppgittDekketAvArbeidsgiver = skattemelding
                       .forForekomsterAv{pendleropphold}
                       .forForekomsterAv{spesifikasjonAvPendlerbolig}
                       .hvorMinstEnForekomst {(fastsatt(antallOvernattingsdoegn) erStørreEnn 0) og
                                              (fastsatt(utbetaltKostgodtgjoerelseFraArbeidsgiver.beloep) ikkeEr oppgitt)
                       }

                    hvis( godtgjoerelseFraarbeidsgiver og
                         ((harOppholdUnderYrkesOgTjenestereise og ikke(harUtslagPaaKontrollForYrkesopphold) og merkostnaderUtenGodtgjørelseLavereEnnSats) eller
                          (harOppholdUnderKjoereoppdrag og merkostnaderUtenGodtgjørelseLavereEnnSats) eller
                          (harPendleropphold og merkostnaderUtenOppgittDekketAvArbeidsgiver)))
                    {
                        betjeningsstrategiSettesTil(ikke, merknadStandard)
                    }
                }
        }

