sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val næringsinntektErSkattefri = skattemelding
            .forForekomsterAv{grunnlagKnyttetTilLottaker}
            .hvorMinstEnForekomst {
                (naeringsinntektForLottaker er oppgitt) og
                    (fastsatt(naeringsinntektForLottaker.trygdeEllerSkattemessigUnntak).finnesBlant(
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftsplikt"
                    ))
            }

      // kontrollen gir utslag skattepliktig har grunnlag for beregningAvSaerskiltFradragForFiskereOgFangstfolk i kort Fradrag for fiske og fangst
      // dersom Inntekter og kostnader fra lott innen fiske og fangst ikke er skattefrie
      // og IKKE har oppgitt antall dager ombord fiskebåt i kort Fradrag for fiske og fangst
      //    eller antall dager ombort er under 130 OG det IKKE er oppgitt vilkårstype i kort Fradrag for fiske og fangst
      // kontrollen gir merknad i SME
      skattemelding
            .forForekomsterAv{saerskiltFradragForFiskereOgFangstfolk}
            .utslagFor {
                val antallDagerOmbordFinnesIkke = forForekomsterAv{driftstidIFiskeOgFangst}
                 .hvorMinstEnForekomst { antallDagerOmbord ikkeEr oppgitt }

                val antallDagerOmbordErMindreEnn130 = forForekomsterAv{driftstidIFiskeOgFangst}
                 .hvorMinstEnForekomst { (antallDagerOmbord erStørreEnn 0) og (antallDagerOmbord erMindreEnn 130) }

                val harIkkeVilkårstype = harIngenForekomsterDer { vilkaarstype er oppgitt }

                val harGrunnlagForBeregningAvSaerskiltFradragForFiskereOgFangstfolk = hvorMinstEnForekomst{grunnlagForBeregningAvSaerskiltFradragForFiskereOgFangstfolk.beloep erStørreEnn 0}

                hvis(harGrunnlagForBeregningAvSaerskiltFradragForFiskereOgFangstfolk og
                     ikke(næringsinntektErSkattefri) og
                     (antallDagerOmbordFinnesIkke eller (antallDagerOmbordErMindreEnn130 og harIkkeVilkårstype))

                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon)
                }
            }
        }

