            .forForekomsterAv { opplysningerOmSkattesubjekt }
            .forForekomsterAv { relasjonTilAnnenPart }
            .hvorMinstEnForekomst { fastsatt(subjektetsRelasjonTilAnnenPart) erLik "ektefelleSaerbo" }

      	val andelPersoninntektUnder100 = naeringsspesifikasjon
      		.forForekomsterAv{fordeltBeregnetPersoninntekt}
            .hvorMinstEnForekomst{andelAvPersoninntektTilordnetInnehaver erUlik 100}

        naeringsspesifikasjon
      		.forForekomsterAv{fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf}
            .utslagFor {

               val andelFordeltSkattemessigResultatUnder100 = hvorMinstEnForekomst{andelAvFordeltSkattemessigResultatTilordnetInnehaver erUlik 100}

                hvis(ikke(erGift) og ikke(harEktefelleSaerbo) og (andelPersoninntektUnder100 eller andelFordeltSkattemessigResultatUnder100)
