sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val finnesIKontrollisteGevinstOgTap = finnesIKontrolliste(
             "Mangler gevinst og tapskonto",
             listeAv(partsnr))

        val verdiFraGevinstOgTapskonto = hentKontrolliste(
             "Mangler gevinst og tapskonto",
             listeAv(partsnr))
            .forForekomsterAv { verdi }
            .summer { verdiSomTall1 }


        val sumOppgittGevinstOgTapskonto = naeringsspesifikasjon
                .forForekomsterAv { spesifikasjonAvAnleggsmiddel.gevinstOgTapskonto }
                .summer{fastsatt(inngaaendeVerdi.beloep)}

        val harGevinsOgTapskontoStoerreEllerLikListeVerdi = sumOppgittGevinstOgTapskonto erStørreEnnEllerLik verdiFraGevinstOgTapskonto

        // kontrollen gir utslag dersom skattyters partsnr finnes i kontrolliste og
        // summen av verdi i feltet innngående verdi 1.januar i kortet for gevinst og tapskonto utenfor virksomhet er blank eller
        //minst 1000 kr lavere enn kandidatlisteverdi
        // skal ikke gi utslag hvis summen av verdi 1.januar i kortet for gevinst og tapskonto i næring er større eller lik kandidatlisteverdi
         skattemelding
            .fokusområde("Fokusområde")
