            .forForekomsterAv{skatteplikt}
            .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        val harOpplysningspliktForKraftverk =  opplysningspliktSdfGjeldende
            .forForekomsterAv{opplysningsplikt}
            .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        val kraftverkGruppert = registrerteKraftverk
            .forForekomsterAv{kraftverk}
            .filtrer{(kraftverknavn er oppgitt) og
                    (loepenummer er oppgitt)}
            .grupperPå { kraftverknavn }

        // Kontroll som gir utslag på alle UP/SDF som er kraftverk i skatteplikt/opplysningsplikt
        // det er manglende eller feil utfylling av løpenr. i tema: Kraftverk-SpesifikasjonAvKraftverk- løpenummer. OPP MOT
        //opplysninger i Skatteetatens fordelingsbase for eiendomsskatt.
        naeringsspesifikasjon
            .forForekomsterAv{kraftverk.spesifikasjonAvKraftverk}
            .utslagFor {

                val harKraftverkLiktNavnOgUliktLoepenummer = filtrer{(fastsatt(navn) er oppgitt) og (fastsatt(loepenummer) er oppgitt)}
                    .hvorMinstEnForekomst { forekomst ->
                        val kraftverkNavn = forekomst.verdiFor { fastsatt(navn) }
                        val loepenummerKraftverk = forekomst.verdiFor { fastsatt(loepenummer) }
                        kraftverkGruppert
                            .forForekomsterIGruppe { kraftverkNavn }
                            .hvorMinstEnForekomst { loepenummer erUlik loepenummerKraftverk.verdi.tekstVerdiOrDefault("") }
                    }

                hvis((harSkattepliktForKreftverk eller harOpplysningspliktForKraftverk) og
                        harKraftverkLiktNavnOgUliktLoepenummer)
                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)
                }
            }
    }

