                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP)
            })
      {
        // kontrollen gir utslag når årets avskrivning er større enn 0,
        //men det ikke er oppgitt enten anskaffelseskostnad, dato for anskaffelse eller forventet levetid, eller
        // hvis årets avskrivning er større enn 0, dato for anskaffelse er tidligere enn i inntektsåret og inngående verdi ikke er oppgitt
        // skal IKKE kjøre og gi utslag på rederi (for UP)
        // kontrollen medfører merknad i valideringstjenesten
       val datoIInntektsaaret = naeringsspesifikasjon
