        naeringsspesifikasjon
          .forForekomsterAv { spesifikasjonAvAnleggsmiddel.lineaertavskrevetAnleggsmiddel }
          .utslagFor {
               val harAvskrivningIkkeAnskaffelseskosttEvervsdatoLevetid =
