                                        ((anskaffelseskost.beloep ikkeEr oppgitt) eller
                                         (ervervsdato ikkeEr oppgitt) eller
                                         (levetid ikkeEr oppgitt)) }

               val harAnskaffelsesdatoAaretFoerOgAvskrivingOgIkkeInngangverdi =
                  hvorMinstEnForekomst{(aaretsAvskrivning.beloep erStørreEnn 0) og
                                       (ervervsdato erFør datoIInntektsaaret) og
                                       (inngaaendeVerdi.beloep ikkeEr oppgitt) }

                hvis(harAvskrivningIkkeAnskaffelseskosttEvervsdatoLevetid eller harAnskaffelsesdatoAaretFoerOgAvskrivingOgIkkeInngangverdi)
                 {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
       }

