                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP) og naeringsspesifikasjon.harInnhold
            })
      {
        // Kontrollen slår ut når det rapporteres påkostning på et driftsmiddel som avskrives lineært (påkostning er større enn 0) og ervervsdatoen er tidligere enn i inntektsåret
        // skal IKKE kjøre og gi utslag på rederi (for UP)
        // Kontrollen medfører avvisning i valideringstjenesten
        val datoIInntektsaaret = naeringsspesifikasjon
            .forForekomsterAv { virksomhet }
            .verdiFor { regnskapsperiode.start }.dato

        naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.lineaertavskrevetAnleggsmiddel }
            .utslagFor {
                hvis((hvorMinstEnForekomst { paakostning.beloep erStørreEnn 0 }) og (verdiFor { ervervsdato }.dato erFør datoIInntektsaaret)) {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil, 1)
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringFoerBeregning)
    .hvis({
         val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

         val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
              .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
              .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }     
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP) og naeringsspesifikasjon.harInnhold

            })
      {
        // Kontrollen kjører på Næring ENK, Selskap, SDF
        // Kontrollen gir utslag hvis årets lineære avskrivning er minst 100 kr høyere enn grunnlag for avskrivning, i kortet for lineær avskrivning.
        // gir utslag pr forekomst av lineær avskrivning som inneholder feil
        // skal IKKE kjøre og gi utslag på rederi (for UP) 
        // Kontrollen medfører avvisning i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.lineaertavskrevetAnleggsmiddel }
            .utslagFor {

                val linearAvskrivningMinusGrunnlagForAvskrivning = 
                 hvorMinstEnForekomst {(aaretsAvskrivning.beloep - grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnn 100) og
                                       (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnn 0) }

                hvis(linearAvskrivningMinusGrunnlagForAvskrivning) {

                    betjeningsstrategiSettesTil(
                      ikke, merknadStandard)
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag dersom det er rapportert verdipapir uten at feltet erOmtattetAvFritaksmetoden er oppgitt
        // kontrollen gir merknad i valideringstjenesten

        skattemeldingUpersonlig
            .utslagFor {
                val harFastsattAksjeIAksjonaerregisteretUtenErOmfattetAvFritaksmetoden = forForekomsterAv{spesifikasjonAvForholdRelevanteForBeskatning.aksjeIAksjonaerregisteret}
                   .hvorMinstEnForekomst{
                     ((fastsatt(selskapetsNavn) er oppgitt) eller (fastsatt(selskapetsOrganisasjonsnummer) er oppgitt)) og
                     (fastsatt(erOmfattetAvFritaksmetoden) ikkeEr oppgitt)
                   }

                val harFastsattAksjeIkkeIAksjonaerregisteretUtenErOmfattetAvFritaksmetoden = forForekomsterAv{spesifikasjonAvForholdRelevanteForBeskatning.aksjeIkkeIAksjonaerregisteret}
                   .hvorMinstEnForekomst{
                     ((fastsatt(selskapetsNavn) er oppgitt) eller (fastsatt(selskapetsOrganisasjonsnummer) er oppgitt)) og
                     (fastsatt(erOmfattetAvFritaksmetoden) ikkeEr oppgitt)
                   }

                val harFastsattAnnetFinansproduktUtenErOmfattetAvFritaksmetoden = forForekomsterAv{spesifikasjonAvForholdRelevanteForBeskatning.annetFinansprodukt}
                   .hvorMinstEnForekomst{
                     ((fastsatt(finansproduktnavn) er oppgitt) eller (fastsatt(kontonummer) er oppgitt)) og
                     (fastsatt(erOmfattetAvFritaksmetoden) ikkeEr oppgitt)
                   }

                val harFastsattVerdipapirfondUtenErOmfattetAvFritaksmetoden = forForekomsterAv{spesifikasjonAvForholdRelevanteForBeskatning.verdipapirfond}
                   .hvorMinstEnForekomst{
                     ((fastsatt(fondetsNavn) er oppgitt) eller (fastsatt(fondetsOrganisasjonsnummer) er oppgitt)) og
                     (fastsatt(erOmfattetAvFritaksmetoden) ikkeEr oppgitt)
                   }

                hvis(harFastsattAksjeIAksjonaerregisteretUtenErOmfattetAvFritaksmetoden eller
                    harFastsattAksjeIkkeIAksjonaerregisteretUtenErOmfattetAvFritaksmetoden eller
                    harFastsattAnnetFinansproduktUtenErOmfattetAvFritaksmetoden eller
                    harFastsattVerdipapirfondUtenErOmfattetAvFritaksmetoden
                    ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning)
     {
        val erLeveringspliktig = opplysningerOmSkattesubjektGjeldendeDokument
            .forForekomsterAv{opplysningerOmSkattesubjekt}
            .hvorMinstEnForekomst{leveringsplikt.harLeveringsplikt erLik true}

        val harOppgittTilstrekkeligInformasjonINS = naeringsspesifikasjon
            .hvorMinstEnForekomst{
                    (fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf er oppgitt) eller
                    (fordeltBeregnetPersoninntekt er oppgitt) eller
                    (balanseregnskap.sumBalanseverdiForEiendel.beloep er oppgitt) eller
                    (balanseregnskap.sumGjeldOgEgenkapital.beloep er oppgitt)
                }

        val kortDeltakerinformasjonSDF = skattemelding
            .forForekomsterAv{deltakersAndelAvFormueOgInntekt}

        // kontrollen gir utslag dersom skattyters er næringsdrivende (harLeveringsplikt = true i OOSS)
        // og ikke har oppgitt
        //       næringsspesifikasjon med tilstrekkelig informasjon
        //             Fordelt beregnet næringsinntekt
        //             eller Personinntekt
        //             eller Samlet gjeld og formuesobjekter
        //       eller opplysninger om deltakers andel i SDF
        //       eller næringsinntekt fordelt fra ektefelle
        // kontrollen gir spesialmerknad i SME
        skattemelding
            .utslagFor {

              val harOppgittNaeringFordeltFraEktefelle = forForekomsterAv{naeringsinntektMvFordeltFraEktefelle}
                 .hvorMinstEnForekomst { fastsatt(naeringstype) er oppgitt }

              val harOppgittDeltakerinformasjonSDF = forForekomsterAv{deltakersAndelAvFormueOgInntekt}
                 .hvorMinstEnForekomst { fastsatt(selskapetsOrganisasjonsnummer) er oppgitt }

            hvis(
                erLeveringspliktig og
                    ikke(harOppgittTilstrekkeligInformasjonINS) og
                    ikke(harOppgittNaeringFordeltFraEktefelle) og
                    ikke(harOppgittDeltakerinformasjonSDF)
            ) {
                betjeningsstrategiSettesTil(ikke, faktiskFeil,
                                           Lenke settesTil kortDeltakerinformasjonSDF)
            }
        }
        }

// Kontrollen gir utslag dersom selskapet ikke svarer på spørsmålene om det er børsnotert eller om det har ytelser
    // mellom aksjonær og selskapet eller temaet Opplysninger om selskapet mangler
    // Kontrollen gir merknad i valideringstjenesten
    sakstype(Upersonlig)
        .kontekst(ValideringSkattepliktig) {

            val manglerOpplysninger = skattemeldingUpersonlig
                .forForekomsterAv { opplysningOmSkattesubjekt }
                .harIngenForekomsterDer {
                    (fastsatt(erBoersnotert) er oppgitt) eller
                        (fastsatt(harYtelseMellomAksjonaerEllerNaerstaaendeOgSelskapEllerSelskapetsDatterselskap) er oppgitt)
                }

            skattemeldingUpersonlig
                .utslagFor {
                    val opplysningOmSkattesubjektErIkkeOppgitt = hvorMinstEnForekomst { fastsatt(opplysningOmSkattesubjekt) ikkeEr oppgitt }

                    hvis(manglerOpplysninger eller opplysningOmSkattesubjektErIkkeOppgitt) {
                        betjeningsstrategiSettesTil(
                            ikke, merknadStandard, 1
                        )
                    }
                }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harSkattested = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { skattested er oppgitt }

        // Gir utslag hvis skattyter mangler skattested i skatteplikt og leverer NSP
        // Gir faktiskFeil i validering og hindrer innsending

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(naeringsinntektMv)
            .utslagFor {

                hvis((kommunenummer ikkeEr oppgitt) og ikke(harSkattested))
                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )

                }
            }
        }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

        val erForhaandsfastsetting = skattemeldingUpersonlig
           .hvorMinstEnForekomst{(fastsatt(forhaandsfastsetting.innsendingsformat) er oppgitt) eller
                                  (grunnlag(forhaandsfastsetting.innsendingsformat) er oppgitt) }

        // kontrollen gir utslag dersom ikke-børsnoterte aksjeselskap ikke rapporterer feltet samletVerdiBakAksjeneISelskapet
        // Skal ikke gjelde forhåndsfastsetting
        // kontrollen gir merknad i valideringstjenesten
        val erIkkeFormuesskattepliktig = skattepliktUpersonligGjeldende
      		.forForekomsterAv{skatteplikt}
            .hvorMinstEnForekomst{erFritattForFormuesskatt erLik true}

      	val selskapetErBoersnotert = skattemeldingUpersonlig
            .forForekomsterAv{opplysningOmSkattesubjekt}
            .hvorMinstEnForekomst { erBoersnotert erLik true }

        val harIkkeVerdiBakAksjene = skattemeldingUpersonlig
            .forForekomsterAv{verdsettingAvAksje}
            .harIngenForekomsterDer { fastsatt(samletVerdiBakAksjeneISelskapet.beloep) er oppgitt }

        skattemeldingUpersonlig
            .utslagFor {

                hvis(ikke(erForhaandsfastsetting) og erIkkeFormuesskattepliktig og
                     harIkkeVerdiBakAksjene og ikke(selskapetErBoersnotert)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard
                    )
                }
            }
    }

sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig) {

      val partsnrUP = skattepliktUpersonligGjeldende
      .verdiFor{skattepliktOgOpplysningerOmSkattesubjekt.partsnummer}

      val partsnrSDF = opplysningspliktSdfGjeldende
        .verdiFor{opplysningspliktOgOpplysningerOmSelskapMedDeltakerfastsetting.partsnummer}

      val harYtelserMellomAksjonaerOgSelskapISMUUP = skattemeldingUpersonlig
           .forForekomsterAv{opplysningOmSkattesubjekt}
           .hvorMinstEnForekomst { harYtelseMellomAksjonaerEllerNaerstaaendeOgSelskapEllerSelskapetsDatterselskap erLik true }

       val harYtelserMellomAksjonaerOgSelskapISMUSDF = selskapsmeldingSdf
           .forForekomsterAv{opplysningOmSelskapMedDeltakerfastsetting}
           .hvorMinstEnForekomst { harYtelseMellomAksjonaerEllerNaerstaaendeOgSelskapEllerSelskapetsDatterselskap erLik true }

      // Skal gi utslag UP og SDF
      // AB-test på skattytere som har partall i siste siffer i partsnummeret
       naeringsspesifikasjon
         .utslagFor {

         val harYtelseFraAksjonaerEllerNaerstaaendeINS = forForekomsterAv { andreForhold.ytelseFraAksjonaerDeltakerEllerNaerstaaende }
            .hvorMinstEnForekomst {
                (laan.kostnadsfoerteRenter erStørreEnn 0) eller
                    (laan.laanesaldoVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (laan.laanesaldoPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (realisasjonOgUtleieAvFormuesobjektMv er oppgitt) }

        val harYtelseFraSelskapINS = forForekomsterAv { andreForhold.ytelseFraSelskapEllerSelskapetsDatterselskap }
            .hvorMinstEnForekomst {
                (totaltUtdeltUtbytteVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (totaltUtdeltUtbytteVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (laan.laanesaldoVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (laan.laanesaldoPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (realisasjonOgUtleieAvFormuesobjektMv er oppgitt) }

          hvis((harYtelserMellomAksjonaerOgSelskapISMUUP og
                (ikke(harYtelseFraAksjonaerEllerNaerstaaendeINS eller harYtelseFraSelskapINS)) og
                (partsnrUP.sisteTegn.finnesBlant("0", "2", "4", "6", "8"))) eller
               (harYtelserMellomAksjonaerOgSelskapISMUSDF og
                (ikke(harYtelseFraAksjonaerEllerNaerstaaendeINS eller harYtelseFraSelskapINS)) og
                (partsnrSDF.sisteTegn.finnesBlant("0", "2", "4", "6", "8"))) )
              {
                 betjeningsstrategiSettesTil(
                   ikke,
                    merknadStandard)
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig)
     .hvis({
          val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
