            .filtrer {
                fastsatt(midlertidigForskjellstype)
                    .finnesBlant("annenPositivMidlertidigForskjell", "annenNegativMidlertidigForskjell")
            }
            .utslagFor {
                hvis(hvorMinstEnForekomst { beskrivelse ikkeEr oppgitt }) {
