sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig)
     .hvis({
          val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
              .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
              .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP)
            })
      {
        // kontrollen gir utslag dersom nedskrevet verdi av utskilte driftsmidler er høyere enn
        // summen av inngående verdi, nyanskaffelse og påkostning
        // skal IKKE kjøre og gi utslag på rederi (for UP)
        // kontrollen gir merknad i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv{spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel}
            .utslagFor {
                val harOppgittNedskrevetVerdiAnleggsmiddel = hvorMinstEnForekomst{nedskrevetVerdiAvUtskiltAnleggsmiddel.beloep er oppgitt}

                val nedskrevetVerdiErStoerreEnnInngaaendeNyanskaffelseOgPaakostning = hvorMinstEnForekomst{nedskrevetVerdiAvUtskiltAnleggsmiddel.beloep erStørreEnn
                            (inngaaendeVerdi.beloep + nyanskaffelse.beloep + paakostning.beloep) }

                hvis(harOppgittNedskrevetVerdiAnleggsmiddel og
                     nedskrevetVerdiErStoerreEnnInngaaendeNyanskaffelseOgPaakostning
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
    }

sakstype(Personlig)
        .kontekst(ValideringSkattepliktig) {
            // kontrollen gir utslag dersom skattyter rapporterer negativ næringsinntekt fra ektefelle uten at det foreligger fastsetting på ektefelle
            // kontrollen gir merknad i SME

          val erGift = skattepliktGjeldende
                .forForekomsterAv { skatteplikt }
                .forForekomsterAv { personligSkattesubjekt.sambeskatningMedAnnenPart }
                .hvorMinstEnForekomst { sambeskatningsrolle erLik "ektefelle" } 

          val ektefelleHarFastsattSkattemelding = utgåtteSkattemeldingEktefelleFelter {
              skattemeldingEktefelleUtgått.hvorMinstEnForekomst { fastsatt(skattemeldingOpprettet.brukeridentifikatortype) er oppgitt }
          }

    utgåtteSkattemeldingFelter {
        skattemeldingUtgått
        .forForekomsterAv(naeringsinntektMvFordeltFraEktefelle)
        .utslagFor {
            hvis(
                erGift og (fastsatt(underskuddSomGruppe.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0) og ikke(
                    ektefelleHarFastsattSkattemelding
                )
            )
            {
                betjeningsstrategiSettesTil(ikke, merknadStandard, skjulerSkatteberegning)
            }
        }
    }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag når skattepliktig har "Negativ alminnelig inntekt overført fra ektefelle" i "Oppsummering og skatteberegning" i grunnlag (negativAlminneligInntektOverfoertFraEktefelle i SKVIS)
        skattemelding
            .utslagFor {

                 val sumNegativAlminneligInntektOverfoertFraEktefelleIGrunnlag = forForekomsterAv{summertSkattegrunnlagForVisning}
                    .forForekomsterAv{negativAlminneligInntektOverfoertFraEktefelle}
                    .summer { grunnlag(beloep) }

            hvis(sumNegativAlminneligInntektOverfoertFraEktefelleIGrunnlag erStørreEnn 0)
              {
                betjeningsstrategiSettesTil(ikke, merknadITopp, ekspander)
              }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringFoerBeregning)
