            .forForekomsterAv { balanseregnskap.gjeldOgEgenkapital.egenkapital }
            .forForekomsterAv { kapital }
            .utslagFor {
                hvis(hvorMinstEnForekomst { (type erLik "2080") og (beloep.beloep erMindreEnn 0) }) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil,
                        1,
                        Gruppering settesTil Næring
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom post 3300 "spesielle offentlige avgifter vedrørende salg" er negativ
        // kontrollen medfører merknad i valideringstjenesten
