sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig)
      .hvis({
          val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
              .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
              .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP)
            })
      {
        // kontrollen gir utslag når grunnlag for inntektsføring er lavere eller lik - 30 000 og avskrivingssats er større enn minimumssatsen for saldogruppen
        // kontrollen gir utslag når grunnlag for inntektsføring er høyere enn - 30 000 og avskrivningssats er mindre enn 100 %
        // skal IKKE kjøre og gi utslag på rederi (for UP)
        // kontrollen medfører merknad i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
            .utslagFor {
                hvis(hvorMinstEnForekomst {
                    (
                        (saldogruppe erLik "a") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnnEllerLik -30_000) og (avskrivningssats erStørreEnn 30)) eller
                            (
                                (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnn -30_000) og
                                    (avskrivningssats erMindreEnn 100)
                                )
                        ) eller
                        (
                            (saldogruppe erLik "c") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnnEllerLik -30_000) og (avskrivningssats erStørreEnn 24)) eller
                                (
                                    (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnn -30_000) og
                                        (avskrivningssats erMindreEnn 100)
                                    )
                            ) eller

                        (
                            (saldogruppe erLik "d") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnnEllerLik -30_000) og (avskrivningssats erStørreEnn 20)) eller
                                (
                                    (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnnEllerLik -30_000) og
                                        (avskrivningssats erMindreEnn 100)
                                    )
                            ) eller
                        (
                            (saldogruppe erLik "j") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnnEllerLik -30_000) og (avskrivningssats erStørreEnn 10)) eller
                                (
                                    (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnnEllerLik -30_000) og
                                        (avskrivningssats erMindreEnn 100)
                                    )
                            )
                }) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val harUtgaaendeVerdiForAnleggsmiddelLavereEnnBeloepsgrenseIFjor = fjoraaretsNaeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
            .filtrer { saldogruppe.finnesBlant("a","c","d","j") }
            .hvorMinstEnForekomst{ utgaaendeVerdi.beloep erMindreEnn -30_000 }

        val harIkkeOppgittAvskrivningssats = naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
            .filtrer{saldogruppe.finnesBlant("a","c","d","j")}
            .harIngenForekomsterDer { fastsatt(avskrivningssats) er oppgitt }

        //Gir utslag hvis utgående verdi for hver forekomst av saldoavskrevetAnleggsmiddel i saldogruppe a, c, d og/eller j i SMIA for forrige inntektsår er lavere enn -30 000
        //og det ikke finnes noen forekomster av avskrivningssats i saldogruppe a, c, d og/eller j.
        //AB-test på skattytere som har tallene 0, 2, 4, 6 eller 8 i siste siffer i partsnummeret
        //kontrollen gir validering i SME
        naeringsspesifikasjon
            .utslagFor {

                hvis(harUtgaaendeVerdiForAnleggsmiddelLavereEnnBeloepsgrenseIFjor og
                     harIkkeOppgittAvskrivningssats og
                     (partsnr.sisteTegn.finnesBlant("0","2","4","6","8"))
                    ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadTips
                    )
                }
            }
    }

sakstype(Personlig,Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig)
        .hvis({
                val storbedriftEllerRederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

                  val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
                      .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
                      .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 val selskapUPIStorbedrift = ansvarligEnhet
                   .hvorMinstEnForekomst{erStorbedrift erLik true}

                   erRederibeskatningsordningBehandlesEtterSaerskilteRegler eller selskapUPIStorbedrift
                }

                val selskapSDFIStorbedrift = hvis((sakstype er SDF)){
                  val selskapIStorbedrift = ansvarligEnhet
                      .hvorMinstEnForekomst{erStorbedrift erLik true}

                   selskapIStorbedrift
                }
               ikke(storbedriftEllerRederibeskatningsordningBehandlesEtterSaerskilteReglerUP) og ikke(selskapSDFIStorbedrift)
            })
      {
     // Dersom grunnlag for årets saldoavskrivning  i saldogruppe b, e, f, g, h eller i < 0  i felt "Grunnlag for avskrivning eller inntektsføring",
     //og hele den negative saldoen ikke er overført til gevinst og tapskonto i felt "Gevinst overført til gevinst- og tapskonto"
     // skal IKKE kjøre og gi utslag på rederi (for UP)
     // og ikke kjøre og gi utslag for storbedrift (for UP og SDF)
     // gir merknad i valideringstjenesten

    naeringsspesifikasjon
        .forForekomsterAv{spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel}
        .filtrer{fastsatt(saldogruppe).finnesBlant("b","e","f","g","h","i")}
       	.utslagFor {
            val grunnlagForSaldoavskrivningOgNegativSaldoIkkeOverfoertTilGevinstOgTapskonto =
               hvorMinstEnForekomst{(fastsatt(vederlagVedRealisasjonOgUttak.beloep) er oppgitt) og
          			(fastsatt(utgaaendeVerdi.beloep) erMindreEnn 0) og
                 	(fastsatt(erRealisasjonenUfrivilligOgGevinstenBetingetSkattefri) erLik false)}

          	hvis(grunnlagForSaldoavskrivningOgNegativSaldoIkkeOverfoertTilGevinstOgTapskonto){
                        betjeningsstrategiSettesTil(
                            ikke,
                            merknadInformasjon
                        )
                    }
                }
        }

