sakstype(SDF)
    .kontekst(ValideringSkattepliktig) {

      val organisasjonErNOKUS = partInfo
        .forForekomsterAv { organisasjonsform }
        .hvorMinstEnForekomst { organisasjonsform erLik "norskkontrollertUtenlandskSelskap" }

     val erDeltaker = selskapsmeldingSdf
       .forForekomsterAv{deltaker}
       .hvorMinstEnForekomst { (deltakerensNorskePersonidentifikator er oppgitt) eller
                               (deltakerensOrganisasjonsnummer er oppgitt) }

     val sumAkkumulertRisk = selskapsmeldingSdf
        .forForekomsterAv{deltaker}
        .summer{riskForUpersonligDeltaker.akkumulertRisk}

      val sumSamletRisk = historiskSelskapsmeldingSdf(EttÅrTilbake)
          {selskapsmeldingSdf ->
            selskapsmeldingSdf
               .forForekomsterAv{deltaker}
               .summer{riskForUpersonligDeltaker.samletRisk}
            }

         // Gir utslag når organisasjonsform er NOKUS
         // og det for DELTAKER identifisert med organisasjonsnummer/fødselsnummer er
         // avvik mellom felt for akkumulert RISK i selskapsmeldingen for 2024
         //OG samletRISK i selskapsmeldingen for 2023
         naeringsspesifikasjon
             .utslagFor {

          val differanseAkkumulertRiskogSamletRisk =
              sumAkkumulertRisk erUlik sumSamletRisk

          hvis(organisasjonErNOKUS og
               erDeltaker og
               differanseAkkumulertRiskogSamletRisk
               )
                  {
            betjeningsstrategiSettesTil(
               ikke, merknadStandard
                )
            }
        }
    }

sakstype(SDF)
