sakstype(Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {

      val selskapetErNokusUP = skattemeldingUpersonlig
         .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
         .hvorMinstEnForekomst{selskapMedDeltakerfastsettingErNokus erLik true}

      val selskapetErNokusSDF = selskapsmeldingSdf
         .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
         .hvorMinstEnForekomst{selskapMedDeltakerfastsettingErNokus erLik true}

      val orgnrIAarUP = skattemeldingUpersonlig
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
          .verdierFor{selskapetsOrganisasjonsnummer}

      val orgnrIFjorFinnesIkkeIAarUP = historiskSkattemeldingUpersonlig(EttÅrTilbake)
          {skattemeldingUpersonlig ->
           skattemeldingUpersonlig
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
          .hvorMinstEnForekomst{(selskapetsOrganisasjonsnummer er oppgitt) og
                                (selskapetsOrganisasjonsnummer.finnesIkkeBlant(orgnrIAarUP))}
          }

      val orgnrIAarSDF = selskapsmeldingSdf
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
          .verdierFor{selskapetsOrganisasjonsnummer}

      val orgnrIFjorFinnesIkkeIAarSDF =historiskSelskapsmeldingSdf(EttÅrTilbake)
          {selskapsmeldingSdf ->
           selskapsmeldingSdf
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
          .hvorMinstEnForekomst{(selskapetsOrganisasjonsnummer er oppgitt) og
                                (selskapetsOrganisasjonsnummer.finnesIkkeBlant(orgnrIAarSDF))}
          }

      // Gir utslag når det er fylt ut kort for Formue og inntekt i selskap med deltakerfastsetting i fjor og
      //IKKE fylt ut kort for Formue og inntekt i selskap med deltakerfastsetting i inntektsåret
      // for selskap med IDENTISK Organisasjonsnummer og
      //det er oppgitt at selskapet er NOKUS
     naeringsspesifikasjon
         .utslagFor {

      hvis(selskapetErNokusUP og
          (orgnrIFjorFinnesIkkeIAarUP) eller
           (selskapetErNokusSDF og
           orgnrIFjorFinnesIkkeIAarSDF))
         {

            betjeningsstrategiSettesTil(
               ikke,
               merknadStandard
                )
            }
        }
    }

sakstype(SDF)
    .kontekst(ValideringSkattepliktig) {

     val harGyldigVirksomhetstype = naeringsspesifikasjon
        .forForekomsterAv { virksomhet }
        .hvorMinstEnForekomst{virksomhetstype erLik "nokus"}

      // Gir utslag når NOKUS er valgt som Virksomhetstype i Næringsspesifikasjonen
      // Ikke krysset av for JA på at Underlagsmateriale kan fremlegges under Opplysninger for SDF eller
      // Underlagsmateriale kan fremlegges under Opplysninger for SDF ikke er oppgitt
      // Fylt ut Andel av samlet underskudd i selskapsmeldingen under Eierforhold, Deltaker
      selskapsmeldingSdf
         .utslagFor {

          val harSvartNeiEllerIkkeOppgittPaaUnderlagsmaterialeForUsdfEllerNokusKanFremlegges = forForekomsterAv{opplysningOmSelskapMedDeltakerfastsetting}
            .hvorMinstEnForekomst{(fastsatt(underlagsmaterialeForUsdfEllerNokusKanFremlegges) ikkeEr oppgitt) eller
                                  (fastsatt(underlagsmaterialeForUsdfEllerNokusKanFremlegges) erUlik true)}

          val harSamletUnderskudd = forForekomsterAv{deltaker}
            .hvorMinstEnForekomst{andelAvSamletUnderskudd er oppgitt}

          hvis(harGyldigVirksomhetstype og
               harSvartNeiEllerIkkeOppgittPaaUnderlagsmaterialeForUsdfEllerNokusKanFremlegges og
               harSamletUnderskudd
              )
                {
            betjeningsstrategiSettesTil(
               ikke,
               merknadStandard
                )
            }
        }
      }

sakstype(SDF)
    .kontekst(ValideringSkattepliktig) {

      val organisasjonErNOKUS = partInfo
         .forForekomsterAv { organisasjonsform }
         .hvorMinstEnForekomst { organisasjonsform erLik "norskkontrollertUtenlandskSelskap" }

      val harAndelAvNettogjeld = selskapsmeldingSdf
          .forForekomsterAv{deltaker}
          .hvorMinstEnForekomst{andelAvNettogjeld erStørreEnn 0}

      // Gir utslag når organisasjonsform er NOKUS og
      // det i Eierforhold er ført opp andel av NETTO GJELD høyere enn null
      naeringsspesifikasjon
         .utslagFor {

      hvis(organisasjonErNOKUS og
           harAndelAvNettogjeld
            )
                {
            betjeningsstrategiSettesTil(
               ikke,
               merknadStandard
                )
            }
        }
    }

