sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig) {

      val organisasjonErNOKUS = partInfo
         .forForekomsterAv { organisasjonsform }
         .hvorMinstEnForekomst { organisasjonsform erLik "norskkontrollertUtenlandskSelskap" }

          // Gir utslag når organisasjonsform er NOKUS og
          // det IKKE er fylt ut Kontaktperson
          // OG/ELLER E-post i Næringsspesifikasjonen i kort Virksomhet
          naeringsspesifikasjon
              .utslagFor {

          val harIkkeKontaktperson = forForekomsterAv{virksomhet}
            .hvorMinstEnForekomst{kontaktperson.navn ikkeEr oppgitt}

            val harIkkeEpost = forForekomsterAv{virksomhet}
            .hvorMinstEnForekomst{kontaktperson.epostadresse ikkeEr oppgitt}

          hvis(organisasjonErNOKUS og
              (harIkkeKontaktperson eller
               harIkkeEpost)
              )
                {
            betjeningsstrategiSettesTil(
               ikke,
               merknadStandard
                )
            }
        }
    }

