      val harAndelAvInntektIProsent = selskapsmeldingSdf
          .forForekomsterAv{deltaker}
          .hvorMinstEnForekomst{(deltakersAndelAvInntektIProsent erStørreEnn 100) eller
                                (deltakersAndelAvInntektIProsent erMindreEnn 0)}

      // Gir utslag når organisasjonsform er NOKUS og
      // det i Eierforhold er ført opp inntektsandel OG/ELLER formuesandel over 100 % ELLER under 0 %
      naeringsspesifikasjon
         .utslagFor {

      hvis(organisasjonErNOKUS og
           harAndelAvFormueIProsent og
           harAndelAvInntektIProsent
            )
                {
            betjeningsstrategiSettesTil(
               ikke,
               merknadStandard
                )
            }
        }
    }

