      // Gjelder for personlige skattepliktige
      // når det i kort for Formue og inntekt i selskap med deltakerfastsetting er
      //oppgitt beløp høyere enn 0 i Andel negativ netto formue
      // OG det er svart JA på at selskapet er NOKUS i samme kort
     skattemelding
        .utslagFor {

     val harSelskapDeltakerfastsettingErNokus = forForekomsterAv{deltakersAndelAvFormueOgInntekt}
        .hvorMinstEnForekomst{fastsatt(selskapMedDeltakerfastsettingErNokus) erLik true}

     val harNegativFormue = forForekomsterAv{deltakersAndelAvFormueOgInntekt}
        .hvorMinstEnForekomst{fastsatt(gjeld.beloep) erStørreEnn 0}

      hvis(harSelskapDeltakerfastsettingErNokus og
           harNegativFormue)

           {
            betjeningsstrategiSettesTil(
               ikke,
               merknadStandard
                )
            }
        }
    }

sakstype(Personlig)
