sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        //kontrollen gir utslag dersom skattyter har valgt 'Ikke skattepliktig eller trygdepliktig' som trygde- og skattemessig unntak for personinntekt,
        //og det mangler trygde- og skattemessig unntak 'Ikke skattepliktig eller trygdepliktig' for næringsinntekten.
        //kontrollen gir merknad i SME

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(naeringsinntektMv)
            .utslagFor {

                val harTrygdefritakPåPersoninntekt = hvorMinstEnForekomst {
                    (samordnetPersoninntekt.personinntekt.beloepSomTall er oppgitt) og
                        (
                            fastsatt(samordnetPersoninntekt.personinntekt.trygdeEllerSkattemessigUnntak) erLik (
                                "skattefriUtenTrygdeavgiftsplikt"
                                )
                            )
                }

                val harIkkeSkattefritakPåNæringsinntekt = hvorMinstEnForekomst {
                    (
                        (naeringsinntektTilordnetInnehaver.beloepSomTall er oppgitt) og
                            (
                                fastsatt(naeringsinntektTilordnetInnehaver.trygdeEllerSkattemessigUnntak) erUlik (
                                    "skattefriUtenTrygdeavgiftsplikt"
                                    )
                                ) eller
                            (
                                (underskuddTilordnetInnehaver.beloepSomTall er oppgitt) og (
                                    fastsatt(underskuddTilordnetInnehaver.trygdeEllerSkattemessigUnntak) erUlik (
                                        "skattefriUtenTrygdeavgiftsplikt"
                                        )
                                    )
                                )
                        )
                }

                hvis(harTrygdefritakPåPersoninntekt og harIkkeSkattefritakPåNæringsinntekt) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig) {

     val selskapIStorbedrift = ansvarligEnhet
      .hvorMinstEnForekomst{erStorbedrift erLik true}

     val rederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
      .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
      .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

     val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
      .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
      .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

     val harOppgittRentepapirMedManglendeInfoUP = skattemeldingUpersonlig
      .forForekomsterAv { spesifikasjonAvForholdRelevanteForBeskatning.obligasjonOgSertifikat }
      .hvorMinstEnForekomst {
                    (rentepapiretsNavn er oppgitt) og
                    ((kontofoerersNavn ikkeEr oppgitt) eller
                     (kontonummer ikkeEr oppgitt) eller
                     (finansproduktidentifikator ikkeEr oppgitt) eller
                     (finansproduktidentifikatortype ikkeEr oppgitt) eller
                     (landkode ikkeEr oppgitt) eller
                     (antallObligasjonOgSertifikat ikkeEr oppgitt))
                }

     val harOppgittRentepapirMedManglendeInfoSDF = selskapsmeldingSdf
      .forForekomsterAv { spesifikasjonAvForholdRelevanteForBeskatning.obligasjonOgSertifikat }
      .hvorMinstEnForekomst {
                    (rentepapiretsNavn er oppgitt) og
                    ((kontofoerersNavn ikkeEr oppgitt) eller
                     (kontonummer ikkeEr oppgitt) eller
                     (finansproduktidentifikator ikkeEr oppgitt) eller
                     (finansproduktidentifikatortype ikkeEr oppgitt) eller
                     (landkode ikkeEr oppgitt) eller
                     (antallObligasjonOgSertifikat ikkeEr oppgitt))
                }

    // Gjelder UP og SDF
    // kontrollen gir utslag dersom skattyter har oppgitt rentepapir med manglende info
    // skal ikke gi utslag dersom skattyter er storbedrift eller skattlegges etter særskilt beskatningsordning for rederiskatteordningen
    // kontrollen gir merknad i valideringstjenesten og SME
     naeringsspesifikasjon
       .utslagFor {

          hvis(ikke(selskapIStorbedrift) og
                ((ikke(rederibeskatningsordningBehandlesEtterSaerskilteRegler) og
                   harOppgittRentepapirMedManglendeInfoUP) eller
                (ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen) og
                  harOppgittRentepapirMedManglendeInfoSDF)))
         {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val finnesIKontrollisteMva = finnesIKontrolliste(
            "Innrapportert i mva",
            listeAv(partsnr))

       // kontrollen gir utslag dersom skattyter (P) finnes i kontrolliste
       // og ikke har rapportert drifsinntekter i NSP
       // AB-test på skattytere som har partall i siste siffer i partsnummeret
       // kontrollen gir merknad
       naeringsspesifikasjon
            .utslagFor {

                 val harIkkeDriftsinntekter = summer { resultatregnskap.driftsinntekt.sumDriftsinntekt.beloep }

                hvis(finnesIKontrollisteMva og
                     (harIkkeDriftsinntekter erLik 0) og
                     (partsnr.sisteTegn.finnesBlant("0", "2", "4", "6", "8"))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon
                    )
                }
            }
        }

sakstype(Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {

      val selskapIkkeIStorbedrift = ansvarligEnhet
        .hvorMinstEnForekomst {erStorbedrift erLik false}

      val rederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemeldingen = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
           .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

      val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
           .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
           .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

        val partsnrUpersonlig = skattepliktUpersonligGjeldende
            .verdiFor {skattepliktOgOpplysningerOmSkattesubjekt.partsnummer}

        val partsnummerSDF = opplysningspliktSdfGjeldende
            .verdiFor{opplysningspliktOgOpplysningerOmSelskapMedDeltakerfastsetting.partsnummer}

        val identifikatorUPOgSDF = partsnrUpersonlig + partsnummerSDF

        val finnesIKontrollisteMva = finnesIKontrolliste(
            "Innrapportert i mva",
            listeAv(identifikatorUPOgSDF))

       // kontrollen gir utslag dersom skattyter (P, UP eller SDF) finnes i kontrolliste
       // og ikke har rapportert drifsinntekter i NSP
       // skal IKKE kjøre og gi utslag på rederi (for UP)
       // og ikke kjøre og gi utslag for storbedrift (for UP og SDF)
       // AB-test på skattytere som har partall i siste siffer i partsnummeret
       // kontrollen gir merknad
       naeringsspesifikasjon
            .utslagFor {

                 val sumDriftsinntekter = summer { resultatregnskap.driftsinntekt.sumDriftsinntekt.beloep }

                hvis(finnesIKontrollisteMva og
                     selskapIkkeIStorbedrift og
                     (sumDriftsinntekter erLik 0) og
                     (((partsnrUpersonlig.sisteTegn.finnesBlant("0", "2", "4", "6", "8")) og ikke(rederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemeldingen)) eller
                      ((partsnummerSDF.sisteTegn.finnesBlant("0", "2", "4", "6", "8")) og ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen)))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning)
      {

        // Gir utslag hvis innsending inneholder verdi i flere felter av

      skattemelding
        .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
        .utslagFor {
            val fiskeOgFangst = hvorMinstEnForekomst{personinntektInnenAnnenNaering.personinntekt.beloep er oppgitt}
            val familiebarnehage = hvorMinstEnForekomst{personinntektInnenFiskeOgFangst.beloep er oppgitt}
            val annenNaering = hvorMinstEnForekomst{personinntektInnenFamiliebarnehageIDeltakersHjem.personinntekt.beloep er oppgitt}


          hvis((fiskeOgFangst og familiebarnehage) eller
               (fiskeOgFangst og annenNaering) eller
               (annenNaering og familiebarnehage)
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil)
               }
            }
       }

