      {

        // Gir utslag hvis innsending gjelder Upersonlig og SDF

      selskapsmeldingSdf
        .forForekomsterAv{deltaker}
        .utslagFor {
                             val fiskeOgFangst = hvorMinstEnForekomst{korreksjonIAlminneligInntekt.arbeidsgodtgjoerelseInnenFiskeOgFangst er oppgitt}
            val familiebarnehage = hvorMinstEnForekomst{korreksjonIAlminneligInntekt.arbeidsgodtgjoerelseInnenFamiliebarnehageIDeltakersHjem er oppgitt}
