          hvis((fiskeOgFangst og familiebarnehage) eller
               (fiskeOgFangst og annenNaering) eller
               (annenNaering og familiebarnehage)
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil)
               }
            }
       }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

      val harErOmfattetAvPetroleumsskatteloven = skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik true}

      val harAnnetFradragKnyttetTilSaerskattegrunnlaget = skattemeldingUpersonlig
        .forForekomsterAv{inntektOgUnderskuddForVirksomhetPaaSokkel}
        .hvorMinstEnForekomst{skattefradragForVirksomhetPaaSokkel.annetFradragKnyttetTilSaerskattegrunnlaget erUlik 0}

      val harAnnetFradragKnyttetTilAlminneligInntektFraVirksomhetPaaSokkel = skattemeldingUpersonlig
        .forForekomsterAv{inntektOgUnderskuddForVirksomhetPaaSokkel}
        .hvorMinstEnForekomst{skattefradragForVirksomhetPaaSokkel.annetFradragKnyttetTilAlminneligInntektFraVirksomhetPaaSokkel erUlik 0}

      val harAnnetFradragKnyttetTilAlminneligInntektFraVirksomhetPaaLand = skattemeldingUpersonlig
        .forForekomsterAv{inntektOgUnderskuddForVirksomhetPaaSokkel}
        .hvorMinstEnForekomst{skattefradragForVirksomhetPaaSokkel.annetFradragKnyttetTilAlminneligInntektFraVirksomhetPaaLand erUlik 0}

        // Gir utslag på upersonlig skattyter som i skatteplikt er omfattet av petroleumsskatteloven
        // Skattemelding/InntektOgUnderskuddForVirksomhetPåSokkel/SkattefradragForVirksomhetPåSokkel/annetFradragKnyttettilSærskattegrunnlaget > 0 eller < 0, eller
        // Skattemelding/InntektOgUnderskuddForVirksomhetPåSokkel/SkattefradragForVirksomhetPåSokkel/annetFradragKnyttetTilAlminneligInntektFraVirksomhetPåSokkel > 0 eller < 0, eller
        // Skattemelding/InntektOgUnderskuddForVirksomhetPåSokkel/SkattefradragForVirksomhetPåSokkel/annetFradragKnyttetTilAlminneligInntektFraVirksomhetPåLand > 0  eller < 0
       naeringsspesifikasjon
         .utslagFor {

          hvis(harErOmfattetAvPetroleumsskatteloven og
                (harAnnetFradragKnyttetTilSaerskattegrunnlaget eller
                  harAnnetFradragKnyttetTilAlminneligInntektFraVirksomhetPaaSokkel eller
                  harAnnetFradragKnyttetTilAlminneligInntektFraVirksomhetPaaLand))
              {
                  betjeningsstrategiSettesTil(
                        ikke, merknadStandard)
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

      val harErOmfattetAvPetroleumsskatteloven = skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik true}

        // Gir utslag på upersonlig skattyter som i skatteplikt er omfattet av petroleumsskatteloven
        // og hvor summen av gitte balanseverdier for anleggsmidler er ulik regnskapsmessig verdi av midlertidige forskjeller Alminnelig inntekt (sokkel og land) og
        //finansinntekt og finanskostnad
        // og MF knyttet til fjerningseiendel og RM verdi av leasingobjekt i balansen (mer ennn 10 000)
       naeringsspesifikasjon
         .utslagFor {

           val sumBalanseverdiForAnleggsmidler = forForekomsterAv { balanseregnskap.anleggsmiddel.balanseverdiForAnleggsmiddel }
              .forForekomsterAv { balanseverdi }
              .filtrer{fastsatt(type).finnesBlant("1000", "1020", "1080", "1101", "1102", "1103", "1104", "1105",
                                                  "1115", "1117", "1120", "1130", "1140", "1150", "1160","1180",
                                                  "1205", "1221", "1225", "1238", "1239", "1280", "1290", "1295")}
              .summer{fastsatt(beloep.beloep)}

           val sumRegnskapsmessigVerdiMidlertidigForskjell = forForekomsterAv{midlertidigForskjellForVirksomhetOmfattetAvPetroleumsskatteloven}
              .filtrer{fastsatt(midlertidigForskjellstype).finnesBlant("driftsmiddelOgGoodwill", "regnskapsmessigVerdiAvLeasingobjekt", "fjerningseiendel")}
              .summer{fastsatt(midlertidigForskjellKnyttetTilAlminneligInntektFraVirksomhetPaaSokkel.regnskapsmessigVerdi.beloep) +
