        // oljesalgOgNormprisinntekt.korreksjonNaarSalgBokfoertIForegaaendeInntektsaarErJustertTilNormprisIInntektsaaretsRegnskap.korreksjonINormprisfradrag -
        //oljesalgOgNormprisinntekt.korreksjonNaarSalgBokfoertIForegaaendeInntektsaarErJustertTilNormprisIInntektsaaretsRegnskap.korreksjonINormpristillegg
         naeringsspesifikasjon
          .forForekomsterAv{oljesalgOgNormprisinntekt}
          .utslagFor {

            val sumKorreksjonIBokfoertSalg =
              summer{korreksjonNaarSalgBokfoertIForegaaendeInntektsaarErJustertTilNormprisIInntektsaaretsRegnskap.korreksjonIBokfoertSalg.beloep}

            val sumKorreksjonINormpristillegg =
              summer{korreksjonNaarSalgBokfoertIForegaaendeInntektsaarErJustertTilNormprisIInntektsaaretsRegnskap.korreksjonINormpristillegg.beloep}

            val sumKorreksjonINormprisfradrag =
              summer{korreksjonNaarSalgBokfoertIForegaaendeInntektsaarErJustertTilNormprisIInntektsaaretsRegnskap.korreksjonINormprisfradrag.beloep}

            val sumKorreksjonINormpristilleggOgKorreksjonINormprisfradrag =
                sumKorreksjonINormprisfradrag - sumKorreksjonINormpristillegg

            val differanseKorreksjonIBokfoertSalgOgSmKorreksjonINormpristilleggOgKorreksjonINormprisfradrag =
               differanse(sumKorreksjonIBokfoertSalg, sumKorreksjonINormpristilleggOgKorreksjonINormprisfradrag)

           hvis(harErOmfattetAvPetroleumsskatteloven og
                (differanseKorreksjonIBokfoertSalgOgSmKorreksjonINormpristilleggOgKorreksjonINormprisfradrag erStørreEnn 100)
                )
              {
                  betjeningsstrategiSettesTil(
                        ikke, merknadStandard)
                }
            }
    }

