sakstype(SDF)
    .kontekst(ValideringSkattepliktig)
    {

        // Gir utslag hvis innsending gjelder SDF
        //Eierforhold.Deltaker.erOmfattetAvRederiskatteordning) = JA
        // SDF har deltaker innenfor rederiskatteordningen
        // og deltakers andel av finansinntekt omfattet av rederiskatteordningen er ulik
        //samlet inntekt omfattet av rederiskatteordningen X selskapets andel i prosent eller
        //deltakers andel i prosent, hvis disse er forskjellige
        // differanse er 100 eller større
     selskapsmeldingSdf
        .utslagFor {

         val harEierforholdDeltakerErOmfattetAvRederiskatteordning = forForekomsterAv{deltaker}
           .hvorMinstEnForekomst{erOmfattetAvRederiskatteordning erLik true}

         val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
            .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

         val sumDeltakersAndelAvFinansinntekt = forForekomsterAv{deltaker}
           .summer{deltakersAndelAvInntektOmfattetAvRederiskatteordningen.andelAvFinansinntekt.beloep}

         val sumSamletInntekt = forForekomsterAv{inntektOgUnderskudd}
           .summer{samletInntektForSelskapMedDeltakerIRederiskatteordning.beloep}

        val sumSelskapetsAndelIProsent = forForekomsterAv{deltaker}
           .summer{selskapsandelIProsent}

         val sumDeltakerssAndelIProsent = forForekomsterAv{deltaker}
           .summer{deltakersAndelAvInntektIProsent}

         val differanseDeltakersAndelInntektUlikInntektSelskapetsAndel = differanse(sumDeltakersAndelAvFinansinntekt, (sumSamletInntekt * sumSelskapetsAndelIProsent/100))

        val differanseDeltakersAndelInntektUlikInntektDeltakserAndel = differanse(sumDeltakersAndelAvFinansinntekt, (sumSamletInntekt * sumDeltakerssAndelIProsent/100))

     hvis(harEierforholdDeltakerErOmfattetAvRederiskatteordning og
           harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen og
            ((differanseDeltakersAndelInntektUlikInntektSelskapetsAndel erStørreEnn 99) eller
             (differanseDeltakersAndelInntektUlikInntektDeltakserAndel erStørreEnn 99))
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)
               }
            }
       }

