            forForekomsterAv { resultatregnskap.finanskostnad }
            .forForekomsterAv { kostnad }
            .filtrer {fastsatt(type) erLik "8150"} 
            .summer{beloep.beloep}  

         val sumTypePermanentForskjell = 
             forForekomsterAv { permanentForskjell }
             .filtrer {fastsatt(permanentForskjellstype) erLik "tilleggForRentekostnad"}
             .summer{beloep.beloep}       

        val sumKostnad = sumRemtekostnadNS + sumAnnenRentekostnadNS + sumTypePermanentForskjell

          hvis((harIkkeErOmfattetAvPetroleumsskattelovenUp og 
                ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding) og
                harBegrensningRentefradragIKonsernOgNaerstaaendeUp og (sumKostnad erStørreEnn 0)) eller
                 (harIkkeErOmfattetAvPetroleumsskattelovenSdf og
                 ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen) og
                 harBegrensningRentefradragIKonsernOgNaerstaaendeUp og (sumKostnad erStørreEnn 0)))
              {
