        (harIkkeNegativEgenkapitalIKonsernregnskapGlobalSdf og
         harIkkePositivEgenkapitalIKonsernregnskapGlobaltSdf)))
       )
              {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)
               }
            }
       }

